INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253596, 'Felbamate', 'Lumacaftor', 'Moderate', 'Coadministration with potent CYP450 3A4 inducers may decrease the plasma concentrations of felbamate, which may be a substrate of the isoenzyme.  Pharmacokinetic data may not be available for all potent inducers with respect to their effect on felbamate.  However, the potent CYP450 3A4 inducers carbamazepine, phenobarbital, and phenytoin have all been shown to decrease felbamate plasma concentrations.', NULL, 'Caution is advised if felbamate is used concomitantly with potent CYP450 3A4 inducers.', NULL, 'Caution is advised if felbamate is used concomitantly with potent CYP450 3A4 inducers.  Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever a potent CYP450 3A4 inducer is added to or withdrawn from therapy with felbamate.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301177/', '[1] "Product Information. Tegretol (carbamazepine)." Novartis Pharmaceuticals  (2002):[2] "Product Information. Rifadin (rifampin)." Hoechst Marion Roussel  (2001):[3] "Product Information. Phenobarbital (phenobarbital)." Lilly, Eli and Company  (2001):[4] "Product Information. Felbatol (felbamate)." Wallace Laboratories  (2001):[5] "Product Information. Dilantin (phenytoin)." Parke-Davis  (2001):[6] "Product Information. Rifamate (rifampin)." Hoechst Marion Roussel  (2001):[7] "Product Information. Carbatrol (carbamazepine)." Athena Neurosciences Inc  (2001):[8] Glue P, Banfield CR, Perhach JL, Mather GG, Racha JK, Levy RH "Pharmacokinetic interactions with felbamate.In vitro-in vivo correlation." Clin Pharmacokinet 33