INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253643, 'Lumacaftor', 'Levacetylmethadol', 'Major', 'Coadministration with inducers of CYP450 3A4 may increase the peak activity and/or decrease the duration of action of levomethadyl acetate, which is primarily metabolized by the isoenzyme.  Induction of CYP450 3A4 also results in increased formation of the active metabolites, nor-LAAM and dinor-LAAM.  High plasma levels of these metabolites may increase the risk of QT interval prolongation and serious ventricular arrhythmias such as torsade de pointes.', NULL, 'Close monitoring is recommended if levomethadyl acetate is used with CYP450 3A4 inducers, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, 'Close monitoring is recommended if levomethadyl acetate is used with CYP450 3A4 inducers, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort.  The dose or dosing interval may require adjustment if an interaction is suspected.  A 12-lead ECG should be performed before initiating levomethadyl acetate therapy, 12 to 14 days after initiating therapy, and periodically thereafter.  Patients should be advised to seek immediate medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301235/', '[1] "Product Information. Orlaam (levomethadyl acetate)." Roxane Laboratories Inc  (2001):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253644, 'Lumacaftor', 'Levonorgestrel', 'Major', 'Coadministration with lumacaftor may significantly decrease the plasma concentrations of contraceptive hormones.  The proposed mechanism is lumacaftor induction of the CYP450 3A4-mediated metabolism of estrogens and progestins.', NULL, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, should not be relied upon as an effective method of contraception when coadministered with lumacaftor/ivacaftor.', NULL, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, should not be relied upon as an effective method of contraception when coadministered with lumacaftor/ivacaftor.  Female patients of childbearing potential should use an alternative or additional method of contraception.  Intrauterine systems are unlikely to be significantly affected because of their local action.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301236/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals  (2015):[3] Faculty of Sexual & Reproductive Healthcare "FSRH Clinical Guidance: Drug Interactions with Hormonal Contraception.  file:///C:/Users/df033684/Downloads/ceuguidancedruginteractionshormonal.pdf"   (2016):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253645, 'Lumacaftor', 'Lidocaine', 'Minor', 'Coadministration with inducers of CYP450 1A2 and/or 3A4 may decrease the plasma concentrations of lidocaine, which is primarily metabolized by these isoenzymes.', NULL, 'Because lidocaine is not available commercially for oral administration, the interaction may be of limited clinical relevance.', NULL, 'Because lidocaine is not available commercially for oral administration, the interaction may be of limited clinical relevance.', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301237/', '[1] Heinonen J, Takki S, Jarho L "Plasma lidocaine levels in patients treated with potential inducers of microsomal enzymes." Acta Anaesthesiol Scand 14 (1970):  89-95[2] Perucca E, Richens A "Reduction of oral bioavailability of lignocaine by induction of first pass metabolism in epileptic patients." Br J Clin Pharmacol 8 (1979):  21-31[3] Perucca E, Ruprah M, Richens A, Park BK, Betteridge DJ, Hedges AM "Effect of low-dose phenobarbitone on five indirect indices of hepatic microsomal enzyme induction and plasma lipoproteins in normal subjects." Br J Clin Pharmacol 12 (1981):  592-6[4] Reichel C, Skodra T, Nacke A, Spengler U, Sauerbruch T "The lignocaine metabolite (MEGX) liver function test and P-450 induction in humans." Br J Clin Pharmacol 46 (1998):  535-9', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253646, 'Lumacaftor', 'Linagliptin', 'Moderate', 'Coadministration with potent inducers of the CYP450 3A4 isoenzyme and/or P-glycoprotein efflux transporter may significantly decrease the plasma concentrations of linagliptin, which is a substrate for both.', NULL, 'Due to the potential for reduced efficacy of linagliptin, coadministration with potent CYP450 3A4 and/or P-glycoprotein inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St.', NULL, 'Due to the potential for reduced efficacy of linagliptin, coadministration with potent CYP450 3A4 and/or P-glycoprotein inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort should generally be avoided.  For patients requiring use of such drugs, an alternative to linagliptin is strongly recommended.  Other known inducers of CYP450 3A4 include aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, efavirenz, modafinil, nafcillin, nevirapine, and various other anticonvulsants, although the extent to which they interact with linagliptin is unknown.  If concomitant use with these agents is necessary, glycemic control should be closely monitored.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301238/', '[1] "Product Information. Tradjenta (linagliptin)." Boehringer Ingelheim  (2011):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253647, 'Lumacaftor', 'Lonafarnib', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of lonafarnib.', NULL, 'Concomitant use of lonafarnib with potent or moderate CYP450 3A4 inducers is considered contraindicated.', NULL, 'Concomitant use of lonafarnib with potent or moderate CYP450 3A4 inducers is considered contraindicated.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301239/', '[1] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals  (2020):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253648, 'Lumacaftor', 'Lorlatinib', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of lorlatinib, which has been found to be metabolized primarily by CYP450 3A4 and UGT1A4 in vitro.  In addition to the potential for diminished efficacy of lorlatinib, the risk of serious hepatotoxicity may be increased due to activation of the pregnane X receptor (PXR) by both lorlatinib and CYP450 3A4 inducers.', NULL, 'Concomitant use of lorlatinib with potent CYP450 3A4 inducers is considered contraindicated.', NULL, 'Concomitant use of lorlatinib with potent CYP450 3A4 inducers is considered contraindicated.  Lorlatinib should not be initiated until after discontinuation of potent CYP450 3A4 inducers for at least 3 plasma half-lives.', 'Metabolism, Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301240/', '[1] "Product Information. Lorbrena (lorlatinib)." Pfizer U.S. Pharmaceuticals Group  (2018):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253649, 'Lumacaftor', 'Lovastatin', 'Major', 'Coadministration with lumacaftor may decrease the plasma concentrations and therapeutic efficacy of drugs that are substrates of CYP450 3A4.  Lumacaftor is a potent CYP450 3A4 inducer in vivo.', NULL, 'Concomitant use of lumacaftor is not recommended with sensitive CYP450 3A4 substrates (e.', NULL, 'Concomitant use of lumacaftor is not recommended with sensitive CYP450 3A4 substrates (e.g., oral midazolam, triazolam, lovastatin, simvastatin) or CYP450 3A4 substrates with a narrow therapeutic index (e.g., immunosuppressants such as cyclosporine, everolimus, sirolimus, and tacrolimus).  Lumacaftor should also be avoided in patients receiving certain antibiotics (e.g., clarithromycin, erythromycin, telithromycin) and azole antifungal agents (e.g., itraconazole, ketoconazole, posaconazole, voriconazole).  Alternatives to these agents should be considered.  Some authorities recommend avoiding coadministration of lumacaftor-ivacaftor and itraconazole from 2 weeks before, during and for 2 weeks after treatment with itraconazole.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301241/', '[1] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceuticals  (2002):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals  (2015):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253650, 'Lurbinectedin', 'Lumacaftor', 'Major', 'Coadministration with a strong or moderate CYP450 3A4 inducer is predicted to decrease lurbinectedin systemic exposure, which may reduce its efficacy.  No drug interaction studies are available for lurbinectedin, but in vitro data suggest that lurbinectedin is metabolized by the CYP450 3A4 isoenzyme.', NULL, 'Concomitant use of lurbinectedin and strong or moderate CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of lurbinectedin and strong or moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301244/', '[1] "Product Information. Zepzelca (lurbinectedin)." Jazz Pharmaceuticals  (2020):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253651, 'Lusutrombopag', 'Lumacaftor', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of lusutrombopag, which is a substrate of both efflux transporter proteins.', NULL, 'Caution is advised if lusutrombopag is prescribed in combination with P-gp and/or BCRP inhibitors.', NULL, 'Caution is advised if lusutrombopag is prescribed in combination with P-gp and/or BCRP inhibitors.  Pharmacologic response to lusutrombopag should be monitored more closely following initiation, discontinuation, or change of dosage of the concomitant P-gp or BCRP inhibitor.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301245/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring.  http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852"   (2013):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253652, 'Macimorelin', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of macimorelin, which is primarily metabolized by the isoenzyme.', NULL, 'The prescribing information for macimorelin recommends discontinuing potent CYP450 3A4 inducers (e.', NULL, 'The prescribing information for macimorelin recommends discontinuing potent CYP450 3A4 inducers (e.g., carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, rifamycins, St. John''s wort) as well as some moderate ones (e.g., bosentan, efavirenz, etravirine, modafinil) prior to macimorelin administration.  A sufficient washout period following discontinuation of the inducers is also advised before using macimorelin.  No recommendations are available for other, less potent CYP450 3A4 inducers; however, it may be appropriate to follow the same precaution.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301246/', '[1] "Product Information. Macrilen (macimorelin)." Aeterna Zentaris  (2018):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253653, 'Maraviroc', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of maraviroc, which is primarily metabolized by the isoenzyme.', NULL, 'Maraviroc should be administered at a dosage of 600 mg twice daily during coadministration with potent CYP450 3A4 inducers.', NULL, 'Maraviroc should be administered at a dosage of 600 mg twice daily during coadministration with potent CYP450 3A4 inducers.  However, if a potent CYP450 3A4 inhibitor such as itraconazole, ketoconazole, delavirdine, clarithromycin, telithromycin, nefazodone, or any protease inhibitor (except tipranavir plus ritonavir) is also used in combination with the inducer, then maraviroc dosage should be reduced to 150 mg twice daily.  Maraviroc is contraindicated for use with potent CYP450 3A4 inducers in patients with severe renal impairment or end-stage renal disease (CrCl <30 mL/min).', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301248/', '[1] "Product Information. Selzentry (maraviroc)." Pfizer U.S. Pharmaceuticals Group  (2007):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253654, 'Medroxyprogesterone acetate', 'Lumacaftor', 'Major', 'Coadministration with lumacaftor may significantly decrease the plasma concentrations of contraceptive hormones.  The proposed mechanism is lumacaftor induction of the CYP450 3A4-mediated metabolism of estrogens and progestins.', NULL, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, should not be relied upon as an effective method of contraception when coadministered with lumacaftor/ivacaftor.', NULL, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, should not be relied upon as an effective method of contraception when coadministered with lumacaftor/ivacaftor.  Female patients of childbearing potential should use an alternative or additional method of contraception.  Intrauterine systems are unlikely to be significantly affected because of their local action.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301251/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals  (2015):[3] Faculty of Sexual & Reproductive Healthcare "FSRH Clinical Guidance: Drug Interactions with Hormonal Contraception.  file:///C:/Users/df033684/Downloads/ceuguidancedruginteractionshormonal.pdf"   (2016):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253655, 'Mefenamic acid', 'Lumacaftor', 'Moderate', 'Coadministration with lumacaftor may decrease the plasma concentrations and therapeutic efficacy of drugs that are substrates of CYP450 3A4, 2B6, 2C19, 2C8, and/or 2C9.  Lumacaftor is a potent inducer of CYP450 3A4 in vivo.', NULL, 'Caution is advised when lumacaftor/ivacaftor is prescribed with drugs that undergo metabolism by CYP450 3A4, 2B6, 2C19, 2C8 and/or 2C9, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when lumacaftor/ivacaftor is prescribed with drugs that undergo metabolism by CYP450 3A4, 2B6, 2C19, 2C8 and/or 2C9, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lumacaftor/ivacaftor is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301252/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals  (2015):[4] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):', 1767365211);
