INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253658, 'Methadone', 'Lumacaftor', 'Major', 'Coadministration with inducers of various CYP450 isoenzymes may decrease the plasma concentrations of methadone, which is metabolized by CYP450 3A4, 2B6, 2C19, 2C9, and 2D6.  Reduced analgesic efficacy or withdrawal symptoms may occur in patients maintained on methadone following the addition of an inducer.  Conversely, discontinuation of the inducer may increase methadone plasma concentrations and potentiate the risk of overdose and fatal respiratory depression.', NULL, 'Caution is advised if methadone is prescribed with CYP450 2B6, 2C19, 2C9 and/or 3A4 inducers.', NULL, 'Caution is advised if methadone is prescribed with CYP450 2B6, 2C19, 2C9 and/or 3A4 inducers.  Pharmacologic response to methadone should be monitored more closely whenever an inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301255/', '[1] Holmes VF "Rifampin-induced methadone withdrawal in AIDS." J Clin Psychopharmacol 10 (1991):  443-4[2] Liu S-J, Wang RI "Case report of barbiturate-induced enhancement of methadone metabolism and withdrawal syndrome." Am J Psychiatry 141 (1984):  1287-8[3] Bell J, Seres V, Bowron P, Lewis J, Batey R "The use of serum methadone levels in patients receiving methadone maintenance." Clin Pharmacol Ther 43 (1988):  623-9[4] Finelli PF "Phenytoin and methadone tolerance." N Engl J Med 294 (1976):  227[5] Tong TG, Pond SM, Kreek MJ, et al. "Phenytoin-induced methadone withdrawal." Ann Intern Med 94 (1981):  349-51[6] Kreek MJ, Garfield JW, Gutjahr CL, Giusti LM "R