INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253724, 'Riociguat', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of riociguat, which is partially metabolized by the isoenzyme.', NULL, 'The potential for diminished therapeutic effects of riociguat should be considered when prescribed in combination with CYP450 3A4 inducers.', NULL, 'The potential for diminished therapeutic effects of riociguat should be considered when prescribed in combination with CYP450 3A4 inducers.  Patients should be closely monitored, and the dosage of riociguat adjusted as necessary.  Data are not available to guide dosing of riociguat when coadministered with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort.  Alternative agents with no or minimal CYP450 3A4-inducing activity should be considered whenever possible.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301336/', '[1] "Product Information. Adempas (riociguat)." Bayer Pharmaceutical Inc  (2013):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253725, 'Ripretinib', 'Lumacaftor', 'Major', 'Coadministration with strong inducers of CYP450 3A4 may decrease the plasma concentrations and anti-tumor activities of ripretinib and its active metabolite (DP-5439).  The proposed mechanism is induction of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of ripretinib and DP-5439.', NULL, 'Concomitant use of ripretinib with a strong CYP450 3A4 inducer is not recommended.', NULL, 'Concomitant use of ripretinib with a strong CYP450 3A4 inducer is not recommended.  Loss of anti-tumor activity may occur.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301337/', '[1] "Product Information. Qinlock (ripretinib)." Deciphera Pharmaceuticals  (2020):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253726, 'Risperidone', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may decrease the plasma concentrations of risperidone and its active metabolite, 9-hydroxyrisperidone.  Both risperidone and 9-hydroxyrisperidone are substrates of the CYP450 3A4 isoenzyme and P-gp efflux transporter.', NULL, 'When risperidone is administered orally, the prescribing information recommends increasing (up to double) the usual dosage gradually (e.', NULL, 'When risperidone is administered orally, the prescribing information recommends increasing (up to double) the usual dosage gradually (e.g., over 1 to 2 weeks) following the addition of a potent CYP450 3A4/P-gp inducer.  For extended-release injectable formulations of risperidone, patients should be closely monitored for the first 4 to 8 weeks after starting treatment with a potent CYP450 3A4/P-gp inducer.  A dosage increase or supplemental oral risperidone therapy may be required in some cases.  Changes in efficacy and safety should be carefully monitored with any dosage adjustment.  Upon discontinuation of the inducer, risperidone dosage should be reassessed and readjusted accordingly for the anticipated increase in plasma concentrations of risperidone and 9-hydroxyrisperidone.  The prescribing information for individual risperidone products should be consulted for specific recommendations regarding concomitant use with potent CYP450 3A4 inducers.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301338/', '[1] "Product Information. Risperdal (risperidone)." Janssen Pharmaceuticals  (2001):[2] Spina E, Avenoso A, Facciola G, Salemi M, Scordo MG, Giacobello T, Madia AG, Perucca E "Plasma concentrations of risperidone and 9-hydroxyrisperidone: Effect of comedication with carbamazepine or valproate." Ther Drug Monit 22 (2000):  481-5[3] Spina E, Scordo MG, Avenoso A, Perucca E "Adverse drug interaction between risperidone and carbamazepine in a patient with chronic schizophrenia and deficient CYP2D6 activity." J Clin Psychopharmacol 21 (2001):  108-9[4] "Product Information. RisperDAL (risperiDONE)." Janssen Pharmaceuticals  (2022):[5] "Product Information. Perseris (risperiDONE)." Indivior Inc.  (2022):[6] "Product Information. RisperDAL Consta (risperiDONE)." Janssen Pharmaceuticals  (2021):[7] "Product Information. Teva-Risperidone (risperidone)." Teva Canada Limited  (2022):[8] "Product Information. Perseris (risperidone)." HLS Therapeutics Inc  (2021):[9] "Product Information. Risperdal Consta (risperidone)." Janssen Inc  (2020):[10] "Product Information. Rixadone (riSPERIDONe)." Alphapharm Pty Ltd  (2022):[11] "Product Information. Risperdal Consta (riSPERIDONe)." Janssen-Cilag Pty Ltd  (2020):[12] "Product Information. Risperidone (risperidone)." Krka UK Ltd  (2023):[13] "Product Information. Okedi (risperidone)." ROVI Biotech Ltd  (2022):[14] "Product Information. Risperdal Consta (risperidone)." Janssen-Cilag Ltd  (2022):', 1767365211);
