INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253772, 'Vemurafenib', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations of vemurafenib, which has been shown in vitro to be a substrate of the isoenzyme.', NULL, 'Concomitant use of vemurafenib with potent CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of vemurafenib with potent CYP450 3A4 inducers should generally be avoided.  Alternative therapeutic agents with less enzyme induction potential should be considered whenever possible during treatment with vemurafenib.  If coadministration is required, the manufacturer recommends increasing the dose of vemurafenib by 240 mg (one tablet) as tolerated.  Close monitoring for toxicities (e.g., QT prolongation, hepatotoxicity, nephrotoxicity, photosensitivity, dermatologic reactions, uveitis) is advised following dose increase.  Two weeks after discontinuation of the strong CYP450 3A4 inducer, the vemurafenib dose that was taken prior to initiating the inducer may be resumed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301399/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Zelboraf (vemurafenib)." Genentech  (2011):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253773, 'Venetoclax', 'Lumacaftor', 'Major', 'Coadministration with potent and moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of venetoclax, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of venetoclax with potent and moderate CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of venetoclax with potent and moderate CYP450 3A4 inducers should generally be avoided.  Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301400/', '[1] "Product Information. Venclexta (venetoclax)." AbbVie US LLC  (2016):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253774, 'Vilazodone', 'Lumacaftor', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations and pharmacologic effects of vilazodone, which is primarily metabolized by the isoenzyme.', NULL, 'The possibility of diminished therapeutic response to vilazodone should be considered during coadministration with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, phenobarbital, phenytoin, and rifampin.', NULL, 'The possibility of diminished therapeutic response to vilazodone should be considered during coadministration with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, phenobarbital, phenytoin, and rifampin.  When concomitant use is prescribed for greater than 14 days, an increase in the vilazodone dosage up to 2-fold may be considered depending on clinical response, up to a maximum of 80 mg daily.  Following discontinuation of the CYP450 3A4 inducer, the dosage of vilazodone should be returned to the original level in 14 days.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301401/', '[1] "Product Information. Viibryd (vilazodone)." Trovis Pharmaceuticals LLC  (2011):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253775, 'Voriconazole', 'Lumacaftor', 'Major', 'Coadministration with lumacaftor may decrease the plasma concentrations and therapeutic efficacy of drugs that are substrates of CYP450 3A4.  Lumacaftor is a potent CYP450 3A4 inducer in vivo.', NULL, 'Concomitant use of lumacaftor is not recommended with sensitive CYP450 3A4 substrates (e.', NULL, 'Concomitant use of lumacaftor is not recommended with sensitive CYP450 3A4 substrates (e.g., oral midazolam, triazolam, lovastatin, simvastatin) or CYP450 3A4 substrates with a narrow therapeutic index (e.g., immunosuppressants such as cyclosporine, everolimus, sirolimus, and tacrolimus).  Lumacaftor should also be avoided in patients receiving certain antibiotics (e.g., clarithromycin, erythromycin, telithromycin) and azole antifungal agents (e.g., itraconazole, ketoconazole, posaconazole, voriconazole).  Alternatives to these agents should be considered.  Some authorities recommend avoiding coadministration of lumacaftor-ivacaftor and itraconazole from 2 weeks before, during and for 2 weeks after treatment with itraconazole.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301404/', '[1] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceuticals  (2002):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals  (2015):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253776, 'Vortioxetine', 'Lumacaftor', 'Moderate', 'Coadministration with potent inducers of CYP450 isoenzymes may significantly decrease the plasma concentrations of vortioxetine, which is primarily metabolized by CYP450 2D6', NULL, 'An increase in the dosage of vortioxetine should be considered when used in combination with potent CYP450 inducers (e.', NULL, 'An increase in the dosage of vortioxetine should be considered when used in combination with potent CYP450 inducers (e.g., carbamazepine, phenobarbital, phenytoin, rifamycins) for greater than 14 days, up to a maximum of three times the original dosage depending on clinical response.  Following discontinuation of the potent CYP450 inducer, vortioxetine dosage should be returned to the original level within 14 days.  Other known CYP450 inducers include aminoglutethimide, barbiturates, bexarotene, bosentan, enzalutamide, efavirenz, etravirine, nevirapine, somatrem, somatropin, and various other anticonvulsants, although the extent to which they interact with vortioxetine is unknown.  If concomitant use is required, close monitoring for signs of reduced therapeutic efficacy is advised, and the vortioxetine dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301405/', '[1] "Product Information. Brintellix (vortioxetine)." Takeda Pharmaceuticals America  (2013):', 1767365211);
