INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253819, 'Coccidioides immitis spherule', 'Trifluridine', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity.  Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents (e.g., greater than or equal to 2 mg/kg/day or 20 mg/day of prednisone or equivalent for 14 consecutive days or more), or long-term topical or inhaled corticosteroids.', NULL, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', NULL, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301468/', '[1] "Product Information. Candin (candida albicans extract)." Nielsen Biosciences Inc  (2001):[2] "Product Information. Histolyn-Cyl (histoplasmin)." ALK Laboratories Inc  (2001):[3] "Product Information. Spherulin (coccidioidin skin test)." ALK Laboratories Inc  (2001):[4] "Product Information. MSTA Mumps Skin Test Antigen (mumps skin test antigen)." Aventis Pharmaceuticals  (2001):[5] "Product Information. Multitest CMI (skin test antigens, multiple)." Aventis Pharmaceuticals  (2001):[6] "Product Information. Tuberculin Tine Test (tuberculin purified protein derivative)." Connaught Laboratories Inc', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253820, 'Crizotinib', 'Tipiracil', 'Moderate', 'Coadministration with crizotinib may theoretically increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 2B6 (e.g., bupropion), P-glycoprotein (P-gp) (e.g., dabigatran), and/or the organic cation transporters 1 and 2 (OCT1, OCT2) (e.g., metformin).', NULL, 'Although the clinical significance is unknown, caution is advised if crizotinib is used concomitantly with drugs that are substrates of CYP450 2B6, P-gp, and/or OCT1 or OCT2, particularly those with a narrow therapeutic range.', NULL, 'Although the clinical significance is unknown, caution is advised if crizotinib is used concomitantly with drugs that are substrates of CYP450 2B6, P-gp, and/or OCT1 or OCT2, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever crizotinib is added to or withdrawn from therapy with these drugs.  Patients should be monitored for the development of adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301469/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Xalkori (crizotinib)." Pfizer U.S. Pharmaceuticals Group  (2011):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253821, 'Dalfampridine', 'Tipiracil', 'Moderate', 'Coadministration with inhibitors and/or substrates of organic cation transporter 2 (OCT2) may increase the plasma concentrations of dalfampridine.  In vitro studies have shown that OCT2 is the primary transporter responsible for the active tubular secretion of dalfampridine, which accounts for approximately 60% of its renal elimination--the major route of clearance for dalfampridine.', NULL, 'Caution is advised when dalfampridine is used with OCT2 inhibitors and/or substrates.', NULL, 'Caution is advised when dalfampridine is used with OCT2 inhibitors and/or substrates.  The potential benefits of concomitant use should be considered against the risk of seizures.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301470/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports.  http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid"   (2007):[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Agencia Espala de Medicamentos y Productos Sanitarios Healthcare "Centro de informaci online de medicamentos de la AEMPS - CIMA.  https://cima.aemps.es/cima/publico/home.html"   (2008):[5] "Product Information. Ampyra (dalfampridine)." Acorda Therapeutics  (2010):[6] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[7] Cerner Multum, Inc "ANVISA Bulio Eletrico." O 0 (2015):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253822, 'Deferiprone', 'Trifluridine', 'Major', 'Coadministration of deferiprone and other drugs that can cause neutropenia or agranulocytosis may increase the risk and/or severity of hematologic toxicity.', NULL, 'Concomitant use of deferiprone with other drugs known to be associated with neutropenia or agranulocytosis should generally be avoided.', NULL, 'Concomitant use of deferiprone with other drugs known to be associated with neutropenia or agranulocytosis should generally be avoided.  Otherwise, close monitoring of absolute neutrophil count (ANC) is recommended if coadministration is required.  The ANC should be measured prior to initiating deferiprone and weekly during therapy.  The manufacturer recommends that treatment be interrupted if neutropenia (ANC between 0.5 and 1.5 x 10^9/L) or infection develops.  Patients should be advised to immediately discontinue deferiprone as well as all other medications with a potential to cause neutropenia and seek medical attention if they experience symptoms indicative of infection such as fever, sore throat, and flu-like symptoms.  A complete blood cell (CBC) count, including a white blood cell (WBC) count corrected for the presence of nucleated red blood cells, an absolute neutrophil count (ANC), and a platelet count should be obtained daily until recovery.  For patients who develop agranulocytosis (ANC less than 0.5 x 10^9/L), hospitalization should be considered, and deferiprone should not be resumed following recovery unless potential benefits outweigh the risks.  Likewise, patients who develop neutropenia with deferiprone should not be rechallenged unless potential benefits outweigh the risks.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301471/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports.  http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid"   (2007):[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Ferriprox (deferiprone)." ApoPharma USA Inc  (2011):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253823, 'Denosumab', 'Trifluridine', 'Moderate', 'Concomitant use of immunosuppressive or myelosuppressive agents with denosumab may increase the risk of serious infections.', NULL, 'Caution is advised if denosumab must be used in combination with immuno- or myelosuppressive agents.', NULL, 'Caution is advised if denosumab must be used in combination with immuno- or myelosuppressive agents.  Patients should be advised to contact their physician if they develop signs and symptoms of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination.  The need for continued denosumab therapy should be assessed when serious infections occur during treatment.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301473/', '[1] "Product Information. Prolia (denosumab)." Amgen USA  (2010):', 1767365211);
