INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253843, 'Idelalisib', 'Trifluridine', 'Moderate', 'The risk and/or severity of diarrhea may be increased when idelalisib is used with other agents that cause diarrhea.', NULL, 'Concomitant use of idelalisib with other drugs that cause diarrhea should be avoided whenever possible.', NULL, 'Concomitant use of idelalisib with other drugs that cause diarrhea should be avoided whenever possible.  Patients treated with idelalisib should be monitored for development of severe diarrhea or colitis during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary.  Permanent discontinuation of idelalisib is recommended in those who experience life-threatening diarrhea or intestinal perforation.  Patients should be advised to contact their physician immediately if the number of bowel movements per day increases by six or more, and to promptly report any new or worsening abdominal pain, chills, fever, nausea, or vomiting.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301499/', '[1] "Product Information. Zydelig (idelalisib)." Gilead Sciences  (2014):', 1767365211);
