INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253850, 'Trifluridine', 'Leflunomide', 'Major', 'The use of leflunomide with other immunosuppressive or myelosuppressive agents may increase the risk of infections.  The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide.', NULL, 'Close monitoring for the development of infection is recommended if leflunomide or teriflunomide is used in patients who are currently receiving or have recently received other immuno- or myelosuppressive agents, and vice versa.', NULL, 'Close monitoring for the development of infection is recommended if leflunomide or teriflunomide is used in patients who are currently receiving or have recently received other immuno- or myelosuppressive agents, and vice versa.  Platelet, white blood cell count, and hemoglobin or hematocrit should be evaluated at baseline and regularly during therapy.  Patients should be advised to contact their physician if they develop signs and symptoms of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination.  If evidence of serious infection or bone marrow suppression occurs, treatment should be stopped, and cholestyramine or charcoal administered to accelerate elimination of leflunomide''s active metabolite from plasma, which otherwise may take up to two years.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301513/', '[1] "Product Information. Arava (leflunomide)." Hoechst Marion Roussel  (2001):[2] "Product Information. Aubagio (teriflunomide)." Genzyme Corporation  (2012):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253851, 'Tipiracil', 'Lenvatinib', 'Moderate', 'Coadministration with inhibitors of the organic cation 2 (OCT2) or multidrug and toxin extrusion 1 (MATE1) transporters may theoretically increase the plasma concentrations of tipiracil, an in vitro substrate of these transporters.  Tipiracil is a thymidine phosphorylase inhibitor and is administered in combination with the antineoplastic thymidine-based nucleoside analog, trifluridine, to increase the systemic exposure of trifluridine.  Increased levels of tipiracil may therefor further increase the systemic exposure of trifluridine, and the risk of adverse effects such as myelosuppression and gastrointestinal toxicity.', NULL, 'Although the clinical significance is unknown, caution is advised if the combination of tipiracil and trifluridine is used concomitantly with drugs that are inhibitors of OCT2 and/or MATE1.', NULL, 'Although the clinical significance is unknown, caution is advised if the combination of tipiracil and trifluridine is used concomitantly with drugs that are inhibitors of OCT2 and/or MATE1.  Patients should be monitored for adverse effects including excessive bone marrow suppression and gastrointestinal toxicity.  Patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms.', 'Metabolism, Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301514/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports.  http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid"   (2007):[3] Cerner Multum, Inc. "Australian Product Information." O 0', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253852, 'Tipiracil', 'Levonorgestrel', 'Moderate', 'Coadministration with tipiracil-trifluridine may reduce the efficacy of contraceptive hormones.', NULL, 'Because trifluridine may cause fetal harm, it is particularly important that patients not become pregnant during tipiracil-trifluridine treatment.', NULL, 'Because trifluridine may cause fetal harm, it is particularly important that patients not become pregnant during tipiracil-trifluridine treatment.  Female patients of childbearing potential using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant tipiracil-trifluridine treatment.  The manufacturer recommends use of additional barrier contraceptive methods during and for up to 6 months after tipiracil-trifluridine therapy.  No recommendations are available for women using hormone-release intrauterine systems or injectable progestin-only contraceptives.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301516/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253853, 'Trifluridine', 'Measles virus vaccine live attenuated', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.  Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy.  A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide).  Current local immunization guidelines should be consulted for recommendations.  In patients who have recently been vaccinated, such therapy should not be initiated for at least 2 weeks (may be longer in some cases; refer to individual product labeling).  Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301517/', '[1] "Product Information. Meruvax II (rubella virus vaccine)." Merck & Co., Inc  (2022):[2] "Product Information. Attenuvax (measles virus vaccine)." Merck & Co., Inc  (2022):[3] "Product Information. YF-V