INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253903, 'Zidovudine', 'Trifluridine', 'Moderate', 'Coadministration with trifluridine may decrease the therapeutic effects of drugs that are substrates of thymidine kinase, such as zidovudine.  The proposed mechanism is competitive inhibition for activation (i.e., phosphorylation) via thymidine kinase.', NULL, 'Caution is advised if trifluridine is used concomitantly with drugs that are substrates of thymidine kinase.', NULL, 'Caution is advised if trifluridine is used concomitantly with drugs that are substrates of thymidine kinase.  Patients should be monitored for loss of therapeutic efficacy.  If the concomitant drug is an antiviral medication, patients should be monitored for loss of antiviral efficacy and an alternative antiviral agent that is not a thymidine kinase substrate, such as lamivudine, zalcitabine, didanosine and/or abacavir should be considered.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301610/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365211);
