INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253953, 'Voxilaprevir', 'Atazanavir', 'Major', 'Coadministration with atazanavir/ritonavir or lopinavir/ritonavir may significantly increase the plasma concentrations of voxilaprevir.  The proposed mechanism is inhibition of organic anion transporting polypeptide (OATP) 1B1-mediated hepatic uptake of voxilaprevir by atazanavir, lopinavir, and ritonavir.  Inhibition of P-glycoprotein-mediated intestinal efflux and CYP450 3A4-mediated metabolism of voxilaprevir may also contribute.', NULL, 'Concomitant use of sofosbuvir/velpatasvir/voxilaprevir with atazanavir- or lopinavir-containing regimens is not recommended.', NULL, 'Concomitant use of sofosbuvir/velpatasvir/voxilaprevir with atazanavir- or lopinavir-containing regimens is not recommended.', 'Metabolism, Absorption, Distribution', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301780/', '[1] "Product Information. Vosevi (sofosbuvir/velpatasvir/voxilaprevir)." Gilead Sciences  (2017):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253954, 'Voxilaprevir', 'Atorvastatin', 'Moderate', 'Coadministration with sofosbuvir/velpatasvir/voxilaprevir may increase the plasma concentrations of all statins including atorvastatin, fluvastatin, lovastatin, and simvastatin.  The proposed mechanism is inhibition of organic anion transporting polypeptide (OATP) 1B1-mediated hepatic uptake of statins by velpatasvir and voxilaprevir.', NULL, 'Caution is advised during concomitant administration of atorvastatin, fluvastatin, lovastatin, simvastatin, or red yeast rice (which contains lovastatin) with sofosbuvir/velpatasvir/voxilaprevir.', NULL, 'Caution is advised during concomitant administration of atorvastatin, fluvastatin, lovastatin, simvastatin, or red yeast rice (which contains lovastatin) with sofosbuvir/velpatasvir/voxilaprevir.  The lowest approved dosage of the statin should be used.  If higher dosages are required, the lowest effective statin dosage should be prescribed based on a risk/benefit assessment.', 'Distribution', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301782/', '[1] "Product Information. Vosevi (sofosbuvir/velpatasvir/voxilaprevir)." Gilead Sciences  (2017):', 1767365211);
