    ) VALUES (1476051, 'Aminophylline', 'Pentoxifylline', 'Moderate', 'Coadministration with pentoxifylline may increase the serum concentrations of theophylline. The mechanism of interaction has not been described, but may involve competitive metabolism as both drugs are methylxanthine derivatives.', NULL, 'Caution is advised if theophylline preparations are used in combination with pentoxifylline.', NULL, 'Caution is advised if theophylline preparations are used in combination with pentoxifylline. Serum theophylline concentrations should be monitored more closely following the initiation, discontinuation or change of dosage of pentoxifylline, and the theophylline dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
