INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (254055, 'Glycopyrronium (topical)', 'Tiotropium', 'Moderate', 'The potential exists for additive anticholinergic effects such as mydriasis, blurred vision, heat intolerance, fever, dry mouth, tachycardia, urinary retention, constipation, and glaucoma (onset or exacerbation) when topical or inhaled anticholinergic agents are used with each other or with other agents that possess anticholinergic properties.  The risk of systemic anticholinergic effects following topical administration depends on variables such as strength of the product, size of the application area, frequency of application, and use of occlusive dressing.  Systemic effects are uncommon following oral inhalation or nasal administration due to the poor absorption of quaternary ammonium compounds from gastrointestinal and nasal mucosa.', NULL, 'Topical and inhaled anticholinergic preparations should preferably not be used in combination with other anticholinergic agents or agents with significant anticholinergic effects such as antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, and class IA antiarrhythmics (especially disopyramide).', NULL, 'Topical and inhaled anticholinergic preparations should preferably not be used in combination with other anticholinergic agents or agents with significant anticholinergic effects such as antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, and class IA antiarrhythmics (especially disopyramide).  Caution is advised if concomitant use cannot be avoided, particularly in the elderly and those with significantly impaired renal and/or hepatic function.  Measures should be taken whenever possible to minimize ocular exposure to these drugs, such as keeping eyes closed during oral inhalation, use of a mouthpiece rather than face mask during nebulization, and not touching the eyes following topical application until hands are washed with soap and water.  Patients should be advised to contact their physician if they experience excessive anticholinergic adverse effects or signs and symptoms of angle-closure glaucoma (e.g., eye pain or discomfort; blurred vision; visual halos; colored images in association with red eyes from conjunctival congestion or corneal edema).', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/302464/', '[1] "Product Information. Atrovent (ipratropium)." Boehringer-Ingelheim  (2002):[2] "Product Information. Combivent (albuterol-ipratropium)." Boehringer-Ingelheim  (2001):[3] "Product Information. Spiriva (tiotropium)." Boehringer Ingelheim  (2002):[4] "Product Information. Tudorza Pressair (aclidinium)." Forest Pharmaceuticals  (2012):[5] Cole JM, Sheehan AH, Jordan JK "Concomitant use of ipratropium and tiotropium in chronic obstructive plmonary disease." Ann Pharmacother 46 (2012):  1717-21[6] "Product Information. Anoro Ellipta (umeclidinium-vilanterol)." GlaxoSmithKline  (2014):[7] "Product Information. Qbrexza (glycopyrrolate topical)." Dermira, Inc.  (2018):[8] "Product Information. Yupelri (revefenacin)." Mylan Specialty  (2018):', 1767365211);
