INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (254099, 'St. John''s Wort', 'Repaglinide', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of repaglinide, which is metabolized by the isoenzyme in the intestine and liver.', NULL, 'Because the antidiabetic effect of repaglinide is dose- and concentration-dependent, patients treated concomitantly with rifampin may require a higher dosage of repaglinide.', NULL, 'Because the antidiabetic effect of repaglinide is dose- and concentration-dependent, patients treated concomitantly with rifampin may require a higher dosage of repaglinide.  Blood glucose should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy in patients stabilized on their existing antidiabetic regimen, and the repaglinide dosage adjusted as necessary.  Patients should be advised to contact their physician if they experience loss of glycemic control.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/302547/', '[1] "Product Information. Prandin (repaglinide)." Novo Nordisk Pharmaceuticals Inc  (2001):[2] Niemi M, Backman JT, Neuvonen M, Neuvonen PJ, Kivisto KT "Rifampin decreases the plasma concentrations and effects of repaglinide." Clin Pharmacol Ther 68 (2000):  495-500[3] Bidstrup TB, Bjornsdottir I, Sidelmann UG, Thomsen MS, Hansen KT "CYP2C8 and CYP3A4 are the principal enzymes involved in the human in vitro biotransformation of the insulin secretagogue repaglinide." Br J Clin Pharmacol 56 (2003):  305-14', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (254100, 'Telotristat ethyl', 'Repaglinide', 'Moderate', 'Coadministration with telotristat ethyl may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4 and/or 2B6 isoenzymes.  The proposed mechanism is accelerated clearance due to induction of CYP450 3A4 and/or 2B6 (in vitro) isoenzymes by telotristat ethyl.', NULL, 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered.', NULL, 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever telotristat ethyl is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/302548/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Xermelo (telotristat ethyl)." Lexicon Pharmaceuticals, Inc.  (2017):', 1767365211);
