INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (254111, 'Phenazopyridine', 'Ketoconazole', 'Major', 'Use of hepatotoxic drugs together with ketoconazole or levoketoconazole may potentiate the risk of liver injury.', NULL, 'The use of ketoconazole or levoketoconazole with other potentially hepatotoxic agents should be avoided.', NULL, 'The use of ketoconazole or levoketoconazole with other potentially hepatotoxic agents should be avoided.  Patients treated with levoketoconazole or ketoconazole should have liver tests performed prior to and during treatment.  Levoketoconazole manufacturer recommends interrupting treatment immediately if signs of hepatotoxicity occur.  Refer to levoketoconazole labeling for specific instructions on management of hepatotoxicity.  Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/302659/', '[1] "Product Information. Ketoconazole (ketoconazole)." Mylan Pharmaceuticals Inc  (2019):[2] "Product Information. Recorlev (levoketoconazole)." Xeris Pharmaceuticals Inc  (2022):[3] Auchus R, Pivonello R, Fleseriu M, et al. "Levoketoconazole: a novel treatment for endogenous Cushing''s syndrome.  https://www.tandfonline.com/doi/pdf/10.1080/17446651.2021.1945440"   (2022):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (254112, 'Butabarbital', 'Nitroprusside', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation.  Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.  Some authorities recommend avoiding alcohol in patients receiving vasodilating antihypertensive drugs.  Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/302661/', '[1] Sternbach H "Fluoxetine-associated potentiation of calcium-channel blockers." J Clin Psychopharmacol 11 (1991):  390-1[2] Feder R "Bradycardia and syncope induced by fluoxetine." J Clin Psychiatry 52 (1991):  139[3] Ellison JM, Milofsky JE, Ely E "Fluoxetine-induced bradycardia and syncope in two patients." J Clin Psychiatry 51 (1990):  385-6[4] Rodriguez de la Torre B, Dreher J, Malevany I, et al. "Serum levels and cardiovascular effects of tricyclic antidepressants and selective serotonin reuptake inhibitors in depressed patients." Ther Drug Monit 23 (2001):  435-40[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Pacher P, Kecskemeti V "Cardiovascular side effects of new antidepressants and antipsychotics: new drugs, old concerns?" Curr Pharm Des 10 (2004):  2463-75[7] Andrews C, Pinner G "Postural hypotension induced by paroxetine." BMJ 316 (1998):  595', 1767365211);
