INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1476730, 'Ranitidine (bismuth citrate)', 'Pexidartinib', 'Moderate', 'INTERVAL: Coadministration with acid-reducing agents may significantly decrease the bioavailability and therapeutic effects of pexidartinib. The proposed mechanism is a pH-dependent reduction in drug dissolution and/or absorption.', 'DDInter', 1767317477, 'If acid-suppression therapy is required, pexidartinib should be administered at least 2 hours before or 10 hours after taking an H2-receptor antagonist or 2 hours before or 2 hours after taking a locally-acting antacid. Concomitant use of pexidartinib with proton pump inhibitors should be avoided.', 'Absorption', 'If acid-suppression therapy is required, pexidartinib should be administered at least 2 hours before or 10 hours after taking an H2-receptor antagonist or 2 hours before or 2 hours after taking a locally-acting antacid.', NULL, NULL, 'Moderate', 'pharmacodynamic');
