INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1476786, 'Tacrolimus', 'Pexidartinib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of sirolimus and tacrolimus, both of which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Given the risk of organ rejection associated with inadequate immunosuppressant drug levels, caution is advised during concomitant therapy with CYP450 3A4 inducers. Sirolimus and tacrolimus blood levels should be checked frequently and the dosage adjusted accordingly whenever a CYP450 3A4 inducer is added to or withdrawn from therapy.', 'Metabolism', 'Given the risk of organ rejection associated with inadequate immunosuppressant drug levels, caution is advised during concomitant therapy with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
