INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1476933, 'Chlorcyclizine', 'Phenelzine', 'Moderate', 'Coadministration of monoamine oxidase inhibitors (MAOIs) and antihistamines may result in additive central nervous system depressant effects. In addition, limited data suggest that MAOIs may potentiate and prolong the anticholinergic effects of antihistamines due to inhibition of catecholamine degradation, which may lead to overstimulation of the sympathetic nervous system.', 'DDInter', 1767317477, 'Prescribing antihistamines in combination with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, procarbazine) should generally be avoided. If concomitant treatment is unavoidable, patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism', 'Prescribing antihistamines in combination with MAOIs or other agents that possess MAOI activity (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
