INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477109, 'Tetryzoline (nasal)', 'Phenelzine', 'Major', 'By inhibiting the breakdown of catecholamines, monoamine oxidase inhibitors (MAOIs) may theoretically potentiate the cardiovascular effects of systemically absorbed sympathomimetic agents following topical administration (e.g., nasal decongestants, ophthalmic vasoconstrictors). However, the interaction has not been reported extensively in the medical literature.', 'DDInter', 1767317477, 'Concomitant use of tetrahydrozoline nasal formulations with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine) is considered contraindicated by the manufacturers.', 'Others', 'Concomitant use of tetrahydrozoline nasal formulations with MAOIs or other agents that possess MAOI activity (e.', NULL, NULL, 'Major', 'pharmacodynamic');
