INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477148, 'Xylometazoline (nasal)', 'Phenelzine', 'Moderate', 'By inhibiting the breakdown of catecholamines, monoamine oxidase inhibitors (MAOIs) may theoretically potentiate the cardiovascular effects of systemically absorbed sympathomimetic agents following topical administration (e.g., nasal decongestants, ophthalmic vasoconstrictors).', 'DDInter', 1767317477, 'Although data are limited, caution may be advisable when topical decongestants and vasoconstrictors are coadministered with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine).', 'Synergism', 'Although data are limited, caution may be advisable when topical decongestants and vasoconstrictors are coadministered with MAOIs or other agents that possess MAOI activity (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477149, 'Yohimbine', 'Phenelzine', 'Moderate', 'Theoretically, yohimbine may precipitate severe hypertensive reactions and hyperpyrexia in patients treated with monoamine oxidase inhibitors (MAOIs). Yohimbine has been shown to induce the release of norepinephrine in patients with essential hypertension. This action could produce a synergistic sympathomimetic effect with MAOIs, which enhance norepinephrine storage in adrenergic neurons.', 'DDInter', 1767317477, 'While clinical data are lacking, yohimbine should preferably not be used with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, procarbazine). At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with yohimbine.', 'Others', 'While clinical data are lacking, yohimbine should preferably not be used with MAOIs or other agents that possess MAOI activity (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
