INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477153, 'Zolmitriptan', 'Phenelzine', 'Major', 'Monoamine oxidase inhibitors (MAOIs) may significantly elevate the plasma concentrations of certain 5-HT1 receptor agonists (namely rizatriptan, sumatriptan, and zolmitriptan) and their active metabolites by inhibiting their metabolic clearance via monoamine oxidase, subtype A. Clinically, this interaction may increase the risk of vasospastic reactions, including coronary artery vasospasm, peripheral vascular ischemia and colonic ischemia, associated with the use of 5-HT1 receptor agonists.', 'DDInter', 1767317477, 'Concomitant use of rizatriptan, sumatriptan, or zolmitriptan with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine) is considered contraindicated. At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with 5-HT1 receptor agonists. If concurrent therapy is necessary, almotriptan, frovatriptan and naratriptan may be preferred due to the lack of a significant pharmacokinetic interaction with MAOIs. However, patients should be closely monitored for adverse effects related to excessive serotonergic activity.', 'Metabolism', 'Concomitant use of rizatriptan, sumatriptan, or zolmitriptan with MAOIs or other agents that possess MAOI activity (e.', NULL, NULL, 'Major', 'pharmacodynamic');
