INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477239, 'Protriptyline', 'Phentermine', 'Moderate', 'The use of tricyclic antidepressants in combination with amphetamines or sympathomimetic appetite suppressants may produce additive cardiovascular effects, increasing the risk of hypertension, cardiac arrhythmias, tachycardia, and fever. The mechanism involves additive pharmacodynamic effects resulting from increased norepinephrine release by sympathomimetic agents and inhibition of norepinephrine reuptake by tricyclic antidepressants. A pharmacokinetic interaction is also possible between tricyclic antidepressants and amphetamines, since many of these agents are metabolized by CYP450 2D6. Increased plasma levels of one or both drugs may occur during coadministration.', 'DDInter', 1767317477, 'Close monitoring of cardiovascular status is recommended for patients receiving this combination. Patients should be advised to promptly report symptoms such as fever, headache, or fast or irregular heartbeats.', 'Metabolism, Synergism', 'Close monitoring of cardiovascular status is recommended for patients receiving this combination.', NULL, NULL, 'Moderate', 'pharmacodynamic');
