INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477277, 'Clofibrate', 'Phenytoin', 'Minor', 'Clofibrate, when coadministered with phenytoin may displace phenytoin from its protein binding sites. A similar reaction may occur with other hydantoin derivatives and other fibric acid derivatives. Patients should be monitored for adverse effects. Patients should be advised to notify their physician if they experience symptoms of toxicity, including drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia. The hydantoin dose may need to be decreased.', 'DDInter', 1767317477, '-', 'Distribution', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
