INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477359, 'Imatinib', 'Phenytoin', 'Major', 'Coadministration with imatinib may increase the plasma concentrations of phenytoin. Imatinib is a potent competitive inhibitor of CYP450 2C9, the isoenzyme primarily responsible for the metabolic clearance of phenytoin. Concomitant use may result in increased risk of phenytoin toxicity.Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of imatinib, which is primarily metabolized by the isoenzyme. Therapeutic failure may occur.', 'DDInter', 1767317477, 'Concomitant use of imatinib with potent CYP450 3A4 inducers such as phenytoin should generally be avoided. Alternative therapeutic agents with less enzyme induction potential should be considered whenever possible. If coadministration with phenytoin is required, some authorities recommend increasing the dosage of imatinib by at least 50% while carefully monitoring clinical response. Imatinib dosages up to 1200 mg/day (600 mg twice daily) have been given to patients receiving concomitant potent CYP450 3A4 inducers. Pharmacologic effects and serum phenytoin levels should also be monitored more closely following the addition, discontinuation, or change of dosage of imatinib. Patients should be advised to contact their physician if they experience loss of seizure control or signs and symptoms of phenytoin toxicity such as nausea, vomiting, tremors, ataxia, lethargy, slurred speech, visual disturbances, and changes in mental status.', 'Metabolism', 'Concomitant use of imatinib with potent CYP450 3A4 inducers such as phenytoin should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477360, 'Imipramine', 'Phenytoin', 'Moderate', 'Tricyclic antidepressants may increase serum phenytoin levels. In vitro studies suggest the mechanism may involve inhibition of CYP450 2C19-catalyzed phenytoin p-hydroxylation by tricyclic antidepressants, especially imipramine and amitriptyline. Phenytoin may induce the hepatic metabolism of desipramine resulting in decreased plasma concentrations.', 'DDInter', 1767317477, 'Clinical monitoring of patient response, tolerance, and serum phenytoin concentrations is recommended. Patients should be advised to notify their doctor if they experience loss of seizure control or symptoms of phenytoin toxicity (drowsiness, visual disturbances, change in mental status, nausea, or ataxia). Phenytoin dosage may need to be adjusted.', 'Metabolism', 'Clinical monitoring of patient response, tolerance, and serum phenytoin concentrations is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477361, 'Human immunoglobulin G (intramuscular)', 'Phenytoin', 'Moderate', 'A possible interaction between phenytoin and intravenous immunoglobulin (IVIG) was suspected in a case of fatal hypersensitivity myocarditis.', 'DDInter', 1767317477, 'Due to the serious nature of the possible interaction, clinicians should consider monitoring hematologic parameters such as eosinophil counts as well as blood pressure and ECG tracings if immunoglobulins are used in patients receiving phenytoin.', 'Others', 'Due to the serious nature of the possible interaction, clinicians should consider monitoring hematologic parameters such as eosinophil counts as well as blood pressure and ECG tracings if immunoglobulins are used in patients receiving phenytoin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
