INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477371, 'Irinotecan (liposomal)', 'Phenytoin', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of irinotecan and its pharmacologically active metabolite, SN-38. Irinotecan is partially metabolized by CYP450 3A4 to inactive substances, and induction of this process results in less of the drug available for conversion to SN-38 via hepatic carboxylesterases. Available data also suggest induction of other enzymatic pathways (e.g., UGT1A1; carboxylesterases) and drug transporters (e.g., multispecific organic anion transporter, or MRP2; mitoxantrone-resistance half transporter protein, or MXR) that may be involved in the clearance of irinotecan and/or SN-38, although the extent to which they contribute to the interaction is unknown.', 'DDInter', 1767317477, 'Concomitant use of irinotecan with potent CYP450 3A4 inducers should generally be avoided. Consideration should be given to substituting nonenzyme-inducing agents at least one to two weeks prior to initiation of irinotecan therapy whenever possible.', 'Metabolism', 'Concomitant use of irinotecan with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477372, 'Isoniazid', 'Phenytoin', 'Moderate', 'Isoniazid frequently increases serum phenytoin levels and may induce phenytoin toxicity in up to 20% of patients receiving the combination. The mechanism is related to inhibition of CYP450 hepatic metabolism by isoniazid. Slow acetylators of isoniazid may be at greater risk for this interaction. Patients should be closely observed for signs of hydantoin toxicity if these drugs must be given concurrently. Other hydantoins may interact similarly.', 'DDInter', 1767317477, 'Serum hydantoin concentrations should be measured if a drug interaction is suspected. Patients should be advised to notify their physician if they experience symptoms of toxicity, including drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia. The hydantoin dose may need to be decreased.', 'Metabolism', 'Serum hydantoin concentrations should be measured if a drug interaction is suspected.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477373, 'Phenytoin', 'Kava', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317477, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477374, 'Phenytoin', 'Levomefolic acid', 'Moderate', 'Anticonvulsants including, but not limited to, carbamazepine, lamotrigine, phenobarbital, phenytoin, primidone, and valproic acid have been shown to impair folate absorption and increase the metabolism of circulating folate. Coadministration with folate therapy may reduce the anticonvulsant effects of phenytoin, phenobarbital, primidone, and succinimides. The exact mechanism of interaction is unknown. Available data pertain primarily to phenytoin.', 'DDInter', 1767317477, 'Caution is advised when folate therapy is coadministered with anticonvulsants. Close monitoring for clinical and laboratory evidence of diminished therapeutic response to both treatments is recommended. Patients should be advised to notify their physician if they experience loss of seizure control.', 'Antagonism', 'Caution is advised when folate therapy is coadministered with anticonvulsants.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477375, 'Phenytoin', 'Leucovorin', 'Moderate', 'Anticonvulsants including, but not limited to, carbamazepine, lamotrigine, phenobarbital, phenytoin, primidone, and valproic acid have been shown to impair folate absorption and increase the metabolism of circulating folate. Coadministration with folate therapy may reduce the anticonvulsant effects of phenytoin, phenobarbital, primidone, and succinimides. The exact mechanism of interaction is unknown. Available data pertain primarily to phenytoin.', 'DDInter', 1767317477, 'Caution is advised when folate therapy is coadministered with anticonvulsants. Close monitoring for clinical and laboratory evidence of diminished therapeutic response to both treatments is recommended. Patients should be advised to notify their physician if they experience loss of seizure control.', 'Antagonism', 'Caution is advised when folate therapy is coadministered with anticonvulsants.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477376, 'Phenytoin', 'Levamisole', 'Moderate', 'Concomitant administration with levamisole and 5-fluorouracil (5-FU) has been reported to increase serum levels of phenytoin. No mechanism has been established for this interaction, and it is unknown whether levamisole or 5-FU, or both, is responsible.', 'DDInter', 1767317477, 'Pharmacologic response to phenytoin should be monitored more closely whenever levamisole, 5-FU, or capecitabine (which is metabolized to 5-FU in vivo) is added to or withdrawn from therapy, and the phenytoin dosage adjusted as necessary. Patients should be advised to notify their physician if they experience symptoms of phenytoin toxicity, including drowsiness, visual disturbances, mental status changes, seizures, nausea, or ataxia. It is uncertain whether this interaction may also occur with other hydantoins.', 'Others', 'Pharmacologic response to phenytoin should be monitored more closely whenever levamisole, 5-FU, or capecitabine (which is metabolized to 5-FU in vivo) is added to or withdrawn from therapy, and the phenytoin dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477377, 'Phenytoin', 'Levobupivacaine', 'Minor', 'Coadministration with inducers of CYP450 1A2 and/or 3A4 may decrease the plasma concentrations of levobupivacaine, which is primarily metabolized by these isoenzymes.', 'DDInter', 1767317477, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477378, 'Phenytoin', 'Levomefolic acid (calcium)', 'Moderate', 'Anticonvulsants including, but not limited to, carbamazepine, lamotrigine, phenobarbital, phenytoin, primidone, and valproic acid have been shown to impair folate absorption and increase the metabolism of circulating folate. Coadministration with folate therapy may reduce the anticonvulsant effects of phenytoin, phenobarbital, primidone, and succinimides. The exact mechanism of interaction is unknown. Available data pertain primarily to phenytoin.', 'DDInter', 1767317477, 'Caution is advised when folate therapy is coadministered with anticonvulsants. Close monitoring for clinical and laboratory evidence of diminished therapeutic response to both treatments is recommended. Patients should be advised to notify their physician if they experience loss of seizure control.', 'Antagonism', 'Caution is advised when folate therapy is coadministered with anticonvulsants.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477379, 'Phenytoin', 'Levacetylmethadol', 'Major', 'Coadministration with inducers of CYP450 3A4 may increase the peak activity and/or decrease the duration of action of levomethadyl acetate, which is primarily metabolized by the isoenzyme. Induction of CYP450 3A4 also results in increased formation of the active metabolites, nor-LAAM and dinor-LAAM. High plasma levels of these metabolites may increase the risk of QT interval prolongation and serious ventricular arrhythmias such as torsade de pointes.', 'DDInter', 1767317477, 'Close monitoring is recommended if levomethadyl acetate is used with CYP450 3A4 inducers, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort. The dose or dosing interval may require adjustment if an interaction is suspected. A 12-lead ECG should be performed before initiating levomethadyl acetate therapy, 12 to 14 days after initiating therapy, and periodically thereafter. Patients should be advised to seek immediate medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', 'Close monitoring is recommended if levomethadyl acetate is used with CYP450 3A4 inducers, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477380, 'Phenytoin', 'Lomitapide', 'Major', 'Coadministration of lomitapide with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Lomitapide can cause elevations in serum transaminases and hepatic steatosis. Clinical data suggest that hepatic fat accumulation is reversible after stopping treatment with lomitapide, although the long-term consequences are unknown.', 'DDInter', 1767317477, 'Caution is advised if lomitapide is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; amiodarone; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; methotrexate; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; tamoxifen; tetracyclines; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; other lipid-lowering medications such as fenofibrate, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients treated with lomitapide should have serum ALT, AST, alkaline phosphatase, and total bilirubin measured prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary. Since alcohol may increase levels of hepatic fat and induce or exacerbate liver injury, the manufacturer recommends that patients taking lomitapide not consume more than one alcoholic drink per day. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'Caution is advised if lomitapide is used with other potentially hepatotoxic agents (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477381, 'Phenytoin', 'Lonafarnib', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of lonafarnib. In vitro, lonafarnib has been shown to be primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 1A2, 2A6, 2C8, 2C9, 2C19, and 2E1.', 'DDInter', 1767317477, 'Concomitant use of lonafarnib with potent or moderate CYP450 3A4 inducers is considered contraindicated.', 'Metabolism', 'Concomitant use of lonafarnib with potent or moderate CYP450 3A4 inducers is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477382, 'Phenytoin', 'Magaldrate', 'Moderate', 'INTERVAL: Concurrent administration of antacids may decrease the bioavailability of phenytoin. The mechanism of interaction is unknown. However, some studies have failed to find a significant interaction with these antacids.', 'DDInter', 1767317477, 'Given the narrow therapeutic index and the large interindividual variability in the pharmacokinetics of phenytoin, patients requiring concomitant antacid therapy should consider separating the times of administration by at least two to three hours if possible. Serum phenytoin levels and seizure activity should be monitored.', 'Absorption', 'Given the narrow therapeutic index and the large interindividual variability in the pharmacokinetics of phenytoin, patients requiring concomitant antacid therapy should consider separating the times of administration by at least two to three hours if possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
