INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477388, 'Phenytoin', 'Methohexital', 'Moderate', 'Barbiturates may induce or inhibit the metabolism of phenytoin. Plasma phenytoin concentrations may be decreased or increased. The mechanism are induction of hepatic CYP450 phenytoin metabolism by therapeutic doses of barbiturates, and competitive inhibition of metabolism by large doses.', 'DDInter', 1767317477, 'Close laboratory and clinical observation for altered effects of both agents is recommended when doses of either agent are started, changed, or discontinued. Patients should be advised to notify their physician if they experience loss of seizure control or excessive or prolonged sedative effects. It may be necessary to alter the dosage of one or both of the drugs.', 'Metabolism', 'Close laboratory and clinical observation for altered effects of both agents is recommended when doses of either agent are started, changed, or discontinued.', NULL, NULL, 'Moderate', 'pharmacodynamic');
