INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477391, 'Phenytoin', 'Metocurine', 'Moderate', 'Case reports have suggested that long-term phenytoin administration may decrease the effectiveness of neuromuscular blocker and increase the dosage requirements. One of the proposed mechanisms is hepatic enzyme induction by phenytoin. Conversely, acute phenytoin administration may increase neuromuscular blocking effects by an unknown mechanism. Other hydantoins may interact with other non-depolarizing neuromuscular blockers in a similar fashion.', 'DDInter', 1767317477, 'Close observation for evidence of altered effects of neuromuscular blocking agents is indicated if these drugs must be used together.', 'Metabolism', 'Close observation for evidence of altered effects of neuromuscular blocking agents is indicated if these drugs must be used together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
