INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477393, 'Phenytoin', 'Nafcillin', 'Minor', 'Nafcillin may displace phenytoin from serum protein-binding sites. Plasma levels of free phenytoin may increase. Some investigators have recommended monitoring of free plasma phenytoin levels in patients who must receive both nafcillin and phenytoin.', 'DDInter', 1767317477, '-', 'Distribution', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477394, 'Phenytoin', 'Norgestrel', 'Major', 'RECOMMENDED: Coadministration with certain anticonvulsants such as carbamazepine, eslicarbazepine, oxcarbazepine, phenobarbital, phenytoin, and primidone may reduce the efficacy of contraceptive hormones. There have been numerous case reports of menstrual abnormalities (e.g., breakthrough bleeding, amenorrhea, irregular menses) and unintended pregnancy occurring in women who received oral contraceptives with anticonvulsants. The interaction stems from accelerated clearance of contraceptive hormones as well as decreased plasma concentrations of unbound (active) hormones due to induction of hepatic CYP450 enzymatic activity and hormone-binding globulin capacity by some anticonvulsants.', 'DDInter', 1767317477, 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with enzyme-inducing anticonvulsants.', 'Metabolism', 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with enzyme-inducing anticonvulsants.', NULL, NULL, 'Major', 'pharmacodynamic');
