INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477396, 'Phenytoin', 'Oxazepam', 'Moderate', 'Coadministration with some benzodiazepines may alter the serum concentrations of phenytoin. Phenytoin toxicity has been reported in patients treated with various benzodiazepines, including clobazam, chlordiazepoxide, clonazepam, and diazepam. Conversely, phenytoin may reduce the plasma concentrations of some benzodiazepines by inducing their metabolism via hepatic microsomal enzymes.', 'DDInter', 1767317477, 'Pharmacologic response and serum hydantoin levels should be monitored more closely whenever a benzodiazepine is added to or withdrawn from therapy, and the hydantoin dosage adjusted as necessary. Patients should be advised to contact their physician if they experience symptoms of hydantoin toxicity such as nausea, vomiting, tremors, ataxia, lethargy, slurred speech, visual disturbances, or changes in mental status. The potential for diminished or inadequate benzodiazepine effects should be considered during concomitant use with phenytoin.', 'Metabolism', 'Pharmacologic response and serum hydantoin levels should be monitored more closely whenever a benzodiazepine is added to or withdrawn from therapy, and the hydantoin dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477397, 'Phenytoin', 'Oxtriphylline', 'Moderate', 'Phenytoin has been reported to increase the clearance of theophylline by 40% to 50%, presumably by enhancing the hepatic CYP450 metabolism of theophylline. Also, coadministration of theophylline has been reported to lower serum phenytoin concentrations. Other hydantoins and hepatically metabolized methylxanthines may also interact.', 'DDInter', 1767317477, 'Management consists of monitoring clinical response and serum concentrations of both drugs and adjusting dosages as necessary. Patients should be advised to report loss of seizure control or worsening of respiratory symptoms to their physician.', 'Metabolism', 'Management consists of monitoring clinical response and serum concentrations of both drugs and adjusting dosages as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477398, 'Phenytoin', 'Paclitaxel (protein-bound)', 'Moderate', 'Theoretically, coadministration with drugs that are inducers of CYP450 2C8 and/or 3A4 may decrease the plasma concentrations of paclitaxel, which is metabolized by these isoenzymes.', 'DDInter', 1767317477, 'Clinicians should recognize the potential for interaction with drugs that induce CYP450 2C8 and/or 3A4 and monitor for evidence of reduced therapeutic response to paclitaxel during coadministration.', 'Metabolism', 'Clinicians should recognize the potential for interaction with drugs that induce CYP450 2C8 and/or 3A4 and monitor for evidence of reduced therapeutic response to paclitaxel during coadministration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
