INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477405, 'Phenytoin', 'Phensuximide', 'Moderate', 'Succinimides may increase serum hydantoin levels. The pharmacologic and toxic effects of hydantoins may be increased. The mechanism is not fully understood, but may be related to inhibition of the hepatic metabolism of hydantoins. In addition, phenytoin has been associated with reductions in succinimide levels.', 'DDInter', 1767317477, 'If a succinimide and a hydantoin must be used together, close observation for evidence of altered hydantoin effect is recommended. Patients should be advised to notify their physician if they experience symptoms of possible hydantoin toxicity, including drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia.', 'Metabolism', 'If a succinimide and a hydantoin must be used together, close observation for evidence of altered hydantoin effect is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477406, 'Pimavanserin', 'Phenytoin', 'Moderate', 'Coadministration with potent or moderate inducers of CYP450 3A4 may decrease the plasma concentrations of pimavanserin, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Coadministration of potent or moderate CYP450 3A4 inducers with pimavanserin should be avoided.', 'Metabolism', 'Coadministration of potent or moderate CYP450 3A4 inducers with pimavanserin should be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
