INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477473, 'Selumetinib', 'Phenytoin', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may decrease the plasma concentrations of selumetinib, which is primarily metabolized by CYP450 3A4 and to a lesser extent by CYP450 2C19, 1A2, 2C9, 2E1, and 3A5. Selumetinib also undergoes glucuronidation by UGT1A1 and UGT1A3.', 'DDInter', 1767317477, 'Concomitant use of selumetinib with potent or moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Concomitant use of selumetinib with potent or moderate CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477474, 'Sertraline', 'Phenytoin', 'Moderate', 'Two case reports suggest that sertraline may significantly increase phenytoin levels when used concomitantly. The mechanism is thought to be related to inhibition of CYP450 2C9-mediated metabolism of phenytoin by sertraline. Other hydantoins may also be affected. In addition, coadministration of sertraline with potent inducers of CYP450 3A4, such as phenytoin, may result in a reduction in the plasma concentrations and therapeutic effects of sertraline. It cannot be excluded that other CYP450 3A4 inducers (e.g., carbamazepine, phenobarbital (phenobarbitone), rifampin, St. John''s wort) may also cause a reduction of sertraline plasma levels.', 'DDInter', 1767317477, 'Clinical and laboratory monitoring for increased hydantoin levels and toxicity is recommended whenever sertraline is added or dosage is changed. Patients should be advised to notify their doctor if they experience symptoms of toxicity (e.g., drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia). In addition, the potential for diminished therapeutic effects of sertraline should be considered when prescribed in combination with phenytoin. Patients should be closely monitored, and the dosage of sertraline adjusted as necessary. Data are not available to guide dosing of sertraline when coadministered with potent CYP450 3A4 inducers. Alternative agents with no or minimal CYP450 3A4-inducing activity may also be considered.', 'Metabolism', 'Clinical and laboratory monitoring for increased hydantoin levels and toxicity is recommended whenever sertraline is added or dosage is changed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
