INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477496, 'Sulfinpyrazone', 'Phenytoin', 'Moderate', 'Coadministration with sulfinpyrazone may significantly increase the plasma concentrations and the risk of toxicity of phenytoin. The mechanism for this interaction has not been clearly delineated, but may involve sulfinpyrazone-mediated inhibition of CYP450 2C9, which is the isoenzyme involved in the metabolism of phenytoin. Displacement of phenytoin from its plasma protein binding sites may also contribute to this interaction.', 'DDInter', 1767317477, 'Caution is advised if sulfinpyrazone is used concomitantly with phenytoin. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever sulfinpyrazone is added to or withdrawn from therapy. Patients should be monitored for the development of phenytoin toxicity such as nausea, vomiting, tremors, ataxia, seizures, drowsiness, lethargy, slurred speech, visual disturbances, and changes in mental status.', 'Metabolism, Distribution', 'Caution is advised if sulfinpyrazone is used concomitantly with phenytoin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477497, 'Sulfisoxazole', 'Phenytoin', 'Moderate', 'Some sulfonamides may inhibit the hepatic metabolism of hydantoins. Serum hydantoin levels and risk of toxicity may be increased. Data are available for sulfadiazine, sulfaphenazole, and sulfamethizole.', 'DDInter', 1767317477, 'Monitoring for clinical and laboratory evidence of altered efficacy and safety is recommended. Hydantoin dosage adjustments or an alternate antimicrobial may be required if an interaction is suspected. Patients should be advised to notify their physician if they experience symptoms of hydantoin toxicity (e.g., drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia).', 'Others', 'Monitoring for clinical and laboratory evidence of altered efficacy and safety is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477498, 'Sunitinib', 'Phenytoin', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations of sunitinib and its pharmacologically active metabolite, both of which are substrates of the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of sunitinib with potent CYP450 3A4 inducers should generally be avoided. Alternative therapeutic agents with minimal or no enzyme induction potential should be considered whenever possible. (Note: This recommendation is based on pharmacokinetic data from healthy volunteers as described above. The safety and efficacy of sunitinib with concomitant CYP450 3A4 inducers have not been established. )', 'Metabolism', 'Concomitant use of sunitinib with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477499, 'Suvorexant', 'Phenytoin', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of suvorexant, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of suvorexant with potent CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy.', 'Metabolism', 'Concomitant use of suvorexant with potent CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477500, 'Tacrolimus', 'Phenytoin', 'Major', 'Coadministration with potent inducers of CYP450 3A4, such as phenytoin, may decrease the plasma concentrations of tacrolimus, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Given the risk of organ rejection associated with inadequate immunosuppressant drug levels, caution is advised during concomitant therapy with CYP450 3A4 inducers. Tacrolimus blood levels should be checked frequently and the dosage adjusted accordingly whenever a CYP450 3A4 inducer is added to or withdrawn from therapy. In addition, serum phenytoin levels should be monitored more closely and the dosage adjusted as necessary whenever tacrolimus is added to or withdrawn from therapy.', 'Metabolism', 'Given the risk of organ rejection associated with inadequate immunosuppressant drug levels, caution is advised during concomitant therapy with CYP450 3A4 inducers.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477501, 'Tadalafil', 'Phenytoin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of tadalafil, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'For the treatment of benign prostatic hyperplasia and erectile dysfunction, no dosage adjustment of tadalafil is warranted during coadministration with CYP450 3A4 inducers. However, the possibility of diminished pharmacologic effects of tadalafil should be considered. For the treatment of pulmonary arterial hypertension, use of tadalafil should be avoided in patients chronically taking potent inducers of CYP450 3A4.', 'Metabolism', 'For the treatment of benign prostatic hyperplasia and erectile dysfunction, no dosage adjustment of tadalafil is warranted during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477502, 'Tapentadol', 'Phenytoin', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317477, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477503, 'Tasimelteon', 'Phenytoin', 'Major', 'Coadministration with potent inducers of CYP450 1A2 and 3A4 may significantly decrease the plasma concentrations of tasimelteon, which is primarily metabolized by these isoenzymes.', 'DDInter', 1767317477, 'Concomitant use of tasimelteon with potent CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy.', 'Metabolism', 'Concomitant use of tasimelteon with potent CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477504, 'Tazemetostat', 'Phenytoin', 'Major', 'Coadministration with moderate or potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of tazemetostat, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of tazemetostat with moderate or potent CYP450 3A4 inducers should generally be avoided. Alternative therapeutic agents with less enzyme induction potential should be considered whenever possible.', 'Metabolism', 'Concomitant use of tazemetostat with moderate or potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477505, 'Telaprevir', 'Phenytoin', 'Major', 'Coadministration with certain anticonvulsants may significantly decrease the plasma concentrations of telaprevir. The mechanism involves induction of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of telaprevir and other protease inhibitors. Anticonvulsants that are considered potent inducers of CYP450 3A4 include carbamazepine, phenobarbital, and phenytoin.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic telaprevir levels, concomitant use with carbamazepine, phenobarbital, or phenytoin is considered contraindicated.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic telaprevir levels, concomitant use with carbamazepine, phenobarbital, or phenytoin is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477506, 'Telithromycin', 'Phenytoin', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of telithromycin, which is primarily metabolized by the isoenzyme. Enzyme activities usually return to normal 14 days after discontinuation of the inducing agent.', 'DDInter', 1767317477, 'Telithromycin should not be used during or within 2 weeks after discontinuation of treatment with a potent CYP450 3A4 inducer.', 'Metabolism', 'Telithromycin should not be used during or within 2 weeks after discontinuation of treatment with a potent CYP450 3A4 inducer.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477507, 'Temazepam', 'Phenytoin', 'Moderate', 'Coadministration with some benzodiazepines may alter the serum concentrations of phenytoin. Phenytoin toxicity has been reported in patients treated with various benzodiazepines, including clobazam, chlordiazepoxide, clonazepam, and diazepam. Conversely, phenytoin may reduce the plasma concentrations of some benzodiazepines by inducing their metabolism via hepatic microsomal enzymes.', 'DDInter', 1767317477, 'Pharmacologic response and serum hydantoin levels should be monitored more closely whenever a benzodiazepine is added to or withdrawn from therapy, and the hydantoin dosage adjusted as necessary. Patients should be advised to contact their physician if they experience symptoms of hydantoin toxicity such as nausea, vomiting, tremors, ataxia, lethargy, slurred speech, visual disturbances, or changes in mental status. The potential for diminished or inadequate benzodiazepine effects should be considered during concomitant use with phenytoin.', 'Metabolism', 'Pharmacologic response and serum hydantoin levels should be monitored more closely whenever a benzodiazepine is added to or withdrawn from therapy, and the hydantoin dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477508, 'Temsirolimus', 'Phenytoin', 'Major', 'Coadministration of temsirolimus with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of sirolimus, a major active metabolite of temsirolimus and known substrate of CYP450 3A4.', 'DDInter', 1767317477, 'Concomitant use of temsirolimus with potent CYP450 3A4 inducers should generally be avoided. If coadministration is required in patients treated with temsirolimus for renal cell carcinoma, the manufacturer recommends increasing the temsirolimus dosage from 25 mg to 50 mg once a week depending on patient tolerability. Based on pharmacokinetic studies, this dosage is predicted to adjust the sirolimus systemic exposure (AUC) to the range observed without inducers. However, clinical data are lacking. The dosage should be reduced to the normally recommended dosage (i.e., 25 mg once a week) following discontinuation of the potent CYP450 3A4 inducer.', 'Metabolism', 'Concomitant use of temsirolimus with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477509, 'Teniposide', 'Phenytoin', 'Moderate', 'Some anticonvulsants such as carbamazepine, phenobarbital, phenytoin, and primidone may reduce the systemic exposure of teniposide. The proposed mechanism is accelerated clearance of teniposide due to induction of hepatic CYP450 enzymes by these anticonvulsants.', 'DDInter', 1767317477, 'During concomitant therapy with certain anticonvulsants, patients should be observed for potentially inadequate or altered response to teniposide, and the dosage adjusted accordingly as necessary.', 'Metabolism', 'During concomitant therapy with certain anticonvulsants, patients should be observed for potentially inadequate or altered response to teniposide, and the dosage adjusted accordingly as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477510, 'Teriflunomide', 'Phenytoin', 'Major', 'The recent, concomitant, or subsequent use (without the recommended leflunomide washout period or procedure) of other agents known to induce hepatotoxicity may potentiate the risk of liver injury associated with leflunomide. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide.', 'DDInter', 1767317477, 'Caution is advised if leflunomide or teriflunomide must be used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice), and vice versa. Liver enzymes and bilirubin should be measured prior to initiation of leflunomide/teriflunomide therapy and at least monthly for the first six months of treatment and every 6 to 8 weeks thereafter. Patients with preexisting liver disease or elevated baseline liver enzymes (i.e., ALT greater than two times ULN) should not receive leflunomide or teriflunomide. Patients who develop elevated serum ALT greater than three times ULN while receiving these medications should discontinue treatment and be given washout procedures with cholestyramine or activated charcoal to accelerate elimination of leflunomide''s active metabolite from plasma, which otherwise may take up to two years. Follow-up monitoring should be conducted at least weekly until the ALT value is within normal range, and washout procedures repeated as necessary. All patients treated with leflunomide or teriflunomide should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'Caution is advised if leflunomide or teriflunomide must be used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477511, 'Tetrabenazine', 'Phenytoin', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317477, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477512, 'Tetracaine (topical)', 'Phenytoin', 'Moderate', 'Some topical anesthetics can be systemically absorbed and cause methemoglobinemia, particularly when applied to mucous membranes. Coadministration with other oxidizing agents that can also induce methemoglobinemia such as injectable local anesthetics, antimalarials (e.g., chloroquine, primaquine, quinine, tafenoquine), nitrates and nitrites, sulfonamides, acetaminophen, aminosalicylic acid, dapsone, dimethyl sulfoxide, flutamide, metoclopramide, nitrofurantoin, phenazopyridine, phenobarbital, phenytoin, and rasburicase may increase the risk. Additional risk factors include very young age (e.g., infants less than 6 months); application to inflamed/abraded areas or broken skin; anemia; cardiac or pulmonary disease; peripheral vascular disease; liver cirrhosis; shock; sepsis; acidosis; and genetic predisposition (e.g., NADH cytochrome-b5 reductase deficiency; glucose-6-phosphate dehydrogenase (G6PD) deficiency; hemoglobin M).', 'DDInter', 1767317477, 'Caution is advised when topical anesthetics are used concomitantly with other methemoglobin-inducing agents. Clinicians should be aware of the potential for methemoglobinemia, particularly when topical anesthetics are applied to mucous membranes or given via the oropharyngeal route. Patients or their caregivers should be advised to seek medical attention if they notice signs and symptoms of methemoglobinemia such as slate-grey cyanosis in buccal mucous membranes, lips, and nail beds; nausea; headache; dizziness; lightheadedness; lethargy; fatigue; dyspnea; tachypnea; tachycardia; palpitation; anxiety; and confusion.', 'Synergism', 'Caution is advised when topical anesthetics are used concomitantly with other methemoglobin-inducing agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477513, 'Thalidomide', 'Phenytoin', 'Moderate', 'Thalidomide can cause peripheral neuropathy, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk and/or severity of nerve damage. Peripheral neuropathy is a common, potentially severe side effect of thalidomide that may be irreversible.', 'DDInter', 1767317477, 'Caution is advised if thalidomide is used in combination with other neurotoxic agents. All patients treated with thalidomide should be examined at monthly intervals for the first three months of therapy and periodically thereafter to detect early signs of neuropathy such as burning, tingling, pain, or numbness in the hands and feet. Electrophysiological testing may be performed at baseline and every six months during therapy to detect asymptomatic neuropathy. Consideration should be given to immediate discontinuation of thalidomide in patients who develop peripheral neuropathy to limit further damage.', 'Synergism', 'Caution is advised if thalidomide is used in combination with other neurotoxic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477514, 'Thallous Chloride', 'Phenytoin', 'Moderate', 'Coadministration of certain drugs may alter the myocardial uptake of thallous chloride Tl-201 and interfere with results of myocardial imaging studies. Drugs that increase or decrease coronary blood flow or potassium uptake may alter the biodistribution of thallous chloride Tl-201.', 'DDInter', 1767317477, 'Clinicians should be aware of possible diagnostic interference by these drugs.', 'Others', 'Clinicians should be aware of possible diagnostic interference by these drugs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477515, 'Thiethylperazine', 'Phenytoin', 'Moderate', 'Concurrent use of phenothiazines and phenytoin may increase the effect of phenytoin and decrease the effect of the phenothiazine.', 'DDInter', 1767317477, 'Close monitoring for clinical and laboratory evidence of phenytoin toxicity is indicated. Patients should be advised to notify their physician if they experience symptoms of possible hydantoin toxicity, including drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia.', 'Others', 'Close monitoring for clinical and laboratory evidence of phenytoin toxicity is indicated.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477516, 'Tioguanine', 'Phenytoin', 'Moderate', 'The concomitant or sequential use of other agents known to induce hepatotoxicity may potentiate the risk of liver injury associated with thioguanine. A high risk of liver toxicity characterized by vascular endothelial damage has been reported with long-term continuous use of thioguanine, particularly in children receiving the drug as part of maintenance therapy for acute lymphoblastic leukemia and in other conditions associated with continuous use.', 'DDInter', 1767317477, 'The risk of hepatic injury should be considered when thioguanine is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Baseline and regular monitoring of hepatic function is recommended. Thioguanine therapy should be discontinued if there is evidence of toxic hepatitis or biliary stasis, as reversal of signs and symptoms of liver toxicity have been reported upon withdrawal. Early indications of liver toxicity are signs associated with portal hypertension such as thrombocytopenia out of proportion with neutropenia and splenomegaly. Elevations of liver enzymes have also been reported, but do not always occur.', 'Synergism', 'The risk of hepatic injury should be considered when thioguanine is used with other potentially hepatotoxic agents (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477517, 'Thioridazine', 'Phenytoin', 'Moderate', 'Concurrent use of phenothiazines and phenytoin may increase the effect of phenytoin and decrease the effect of the phenothiazine.', 'DDInter', 1767317477, 'Close monitoring for clinical and laboratory evidence of phenytoin toxicity is indicated. Patients should be advised to notify their physician if they experience symptoms of possible hydantoin toxicity, including drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia.', 'Others', 'Close monitoring for clinical and laboratory evidence of phenytoin toxicity is indicated.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477518, 'Thiotepa', 'Phenytoin', 'Moderate', 'Coadministration with phenytoin may result in significantly decreased plasma concentrations of cyclophosphamide and thiotepa, while concentrations of their main active metabolites increase. The proposed mechanism is induction of CYP450 2B6 metabolism by phenytoin.', 'DDInter', 1767317477, 'Given the magnitude of the interaction, use of cyclophosphamide or thiotepa in combination with phenytoin should be avoided if possible. Anticonvulsants with no significant effects on CYP450 hepatic enzymes such as valproic acid, lamotrigine, or gabapentin may be appropriate alternatives. If phenytoin is required, consideration should be given to dosage reduction of the chemotherapeutic agents. In addition, plasma levels of the active metabolites should be monitored to guide further dosing.', 'Metabolism', 'Given the magnitude of the interaction, use of cyclophosphamide or thiotepa in combination with phenytoin should be avoided if possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477519, 'Thiothixene', 'Phenytoin', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317477, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477520, 'Tiagabine', 'Phenytoin', 'Moderate', 'Population pharmacokinetic studies indicate that tiagabine clearance is 60% greater in patients receiving concomitant phenytoin with or without other enzyme inducing antiepileptic drugs (AEDs). The mechanism is thought to be induction of the CYP450 3A4 metabolism of tiagabine by phenytoin.', 'DDInter', 1767317477, 'Clinical and laboratory monitoring may be necessary if these medications are used together, especially if phenytoin is started or stopped in a patient stabilized on tiagabine. Patients should be advised to notify their physician if they experience loss of seizure control.', 'Metabolism', 'Clinical and laboratory monitoring may be necessary if these medications are used together, especially if phenytoin is started or stopped in a patient stabilized on tiagabine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477521, 'Ticagrelor', 'Phenytoin', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of ticagrelor, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of ticagrelor with potent CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Concomitant use of ticagrelor with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477522, 'Ticlopidine', 'Phenytoin', 'Moderate', 'The coadministration with ticlopidine may increase the plasma concentrations and pharmacologic effects of phenytoin. The proposed mechanism is ticlopidine inhibition of CYP450 2C19, one of the isoenzymes responsible for the metabolic clearance of phenytoin.', 'DDInter', 1767317477, 'Given their narrow therapeutic index, caution is advised if phenytoin or other hydantoins must be used concomitantly with ticlopidine. Hydantoin levels and pharmacologic effects should be closely monitored and the dosage adjusted accordingly, particularly following initiation, discontinuation or change of dosage of ticlopidine in patients who are stabilized on their anticonvulsant regimen. Patients should be advised to contact their physician if they develop signs of phenytoin toxicity such as ataxia, dizziness, nausea, vomiting, and visual disturbances.', 'Metabolism', 'Given their narrow therapeutic index, caution is advised if phenytoin or other hydantoins must be used concomitantly with ticlopidine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477523, 'Tinidazole', 'Phenytoin', 'Moderate', 'Coadministration with a nitroimidazole may increase the serum concentrations of phenytoin. The interaction has been reported with metronidazole, and the proposed mechanism is inhibition of phenytoin metabolism via CYP450 2C9.', 'DDInter', 1767317477, 'Pharmacologic response and serum phenytoin levels should be monitored more closely whenever metronidazole is added to or withdrawn from therapy, and the phenytoin (or fosphenytoin) dosage adjusted as necessary. Patients should be advised to notify their physician if they experience potential symptoms of phenytoin toxicity such as nausea, vomiting, nystagmus, slurred speech, lethargy, tremor, ataxia, hyperreflexia, and dysarthria. Given its structural similarities to metronidazole, the same precaution may be applicable during therapy with tinidazole, although clinical data are lacking.', 'Metabolism', 'Pharmacologic response and serum phenytoin levels should be monitored more closely whenever metronidazole is added to or withdrawn from therapy, and the phenytoin (or fosphenytoin) dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477524, 'Tipranavir', 'Phenytoin', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477525, 'Tizanidine', 'Phenytoin', 'Moderate', 'One case report suggests elevated phenytoin levels associated with concomitant tizanidine use. The mechanism is unknown, but postulated to be competitive inhibition of hepatic enzymes.', 'DDInter', 1767317477, 'Until further information is available close clinical and laboratory monitoring for toxicity is recommended. Dose adjustment may be necessary if an interaction is suspected. Patients should be advised to notify their physician if they experience symptoms of possible hydantoin toxicity, including drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia.', 'Metabolism', 'Until further information is available close clinical and laboratory monitoring for toxicity is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477526, 'Tocilizumab', 'Phenytoin', 'Moderate', 'Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin (IL) inhibitors, tumor necrosis factor (TNF) blockers, or interferon (IFN) inhibitors in patients with chronic inflammatory diseases. Because the formation of hepatic CYP450 enzymes is down-regulated during infection and chronic inflammation by increased levels of certain cytokines (e.g., interleukins-1, -6, and -10; tumor necrosis factor alpha; interferons), treatment targeting these cytokines may restore or normalize CYP450 enzyme levels resulting in increased metabolism of these drugs.', 'DDInter', 1767317477, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.g., antiarrhythmics, anticonvulsants, immunosuppressants, theophylline) or sensitive substrates where decreases in plasma levels may be significant or undesirable (e.g., oral contraceptives, statins, benzodiazepines, opioids). Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of such treatments, and the dosage(s) of the CYP450 substrate(s) adjusted accordingly. The effects of IL inhibitors, TNF blockers, and IFN inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477527, 'Tofacitinib', 'Phenytoin', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of tofacitinib, which is primarily metabolized by the isoenzyme. The interaction may result in loss of or reduced clinical response.', 'DDInter', 1767317477, 'The use of tofacitinib in combination with potent CYP450 3A4 inducers should generally be avoided. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible. The extent to which moderate CYP450 3A4 inducers interact with tofacitinib is unknown.', 'Metabolism', 'The use of tofacitinib in combination with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477528, 'Tolazamide', 'Phenytoin', 'Moderate', 'The coadministration with phenytoin may diminish the hypoglycemic effect of sulfonylureas. Phenytoin has been reported to cause hyperglycemia, hypoinsulinemia and glucose intolerance, thus it may interfere with blood glucose control. Conversely, some sulfonylureas may enhance the plasma concentrations and pharmacologic effects of phenytoin by competitive inhibition of its metabolism by CYP450 2C9 and/or 2C19 isoenzymes. Phenytoin intoxication has been reported during coadministration with tolbutamide specifically.', 'DDInter', 1767317477, 'Consideration should be given to more closely monitoring blood glucose and phenytoin levels during coadministration with sulfonylureas. Patients should be advised to notify their physician if loss of glycemic control occurs or if they experience symptoms of possible phenytoin toxicity, including drowsiness, visual disturbances, change in mental status, seizures, nausea, and ataxia.', 'Metabolism, Antagonism', 'Consideration should be given to more closely monitoring blood glucose and phenytoin levels during coadministration with sulfonylureas.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477529, 'Tolbutamide', 'Phenytoin', 'Moderate', 'The coadministration with phenytoin may diminish the hypoglycemic effect of sulfonylureas. Phenytoin has been reported to cause hyperglycemia, hypoinsulinemia and glucose intolerance, thus it may interfere with blood glucose control. Conversely, some sulfonylureas may enhance the plasma concentrations and pharmacologic effects of phenytoin by competitive inhibition of its metabolism by CYP450 2C9 and/or 2C19 isoenzymes. Phenytoin intoxication has been reported during coadministration with tolbutamide specifically.', 'DDInter', 1767317477, 'Consideration should be given to more closely monitoring blood glucose and phenytoin levels during coadministration with sulfonylureas. Patients should be advised to notify their physician if loss of glycemic control occurs or if they experience symptoms of possible phenytoin toxicity, including drowsiness, visual disturbances, change in mental status, seizures, nausea, and ataxia.', 'Metabolism, Antagonism', 'Consideration should be given to more closely monitoring blood glucose and phenytoin levels during coadministration with sulfonylureas.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477530, 'Tolcapone', 'Phenytoin', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317477, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477531, 'Toremifene', 'Phenytoin', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of toremifene.', 'DDInter', 1767317477, 'Concomitant use of toremifene with potent CYP450 3A4 inducers should generally be avoided. Alternative therapeutic agents with less enzyme induction potential should be considered whenever possible during treatment with toremifene. If coadministration is required, a doubling of the toremifene dosage may be necessary depending on patient tolerability. Close monitoring for toxicities (e.g., QT prolongation, hepatotoxicity, hypercalcemia and tumor flare) is recommended if the dosage of toremifene is increased. The dosage should be reduced to the indicated dosage following discontinuation of the potent CYP450 3A4 inducer.', 'Metabolism', 'Concomitant use of toremifene with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477532, 'Torasemide', 'Phenytoin', 'Minor', 'Some hydantoins may reduce the gastrointestinal absorption of some loop diuretics. Serum diuretic concentrations and diuretic effects after oral dosing may be decreased. Data are available for furosemide and phenytoin only. Increases in diuretic dosage may be necessary.', 'DDInter', 1767317477, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477533, 'Trabectedin', 'Phenytoin', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations of trabectedin, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Given the potential for diminished pharmacologic effects of trabectedin in the presence of potent CYP450 3A4 inducers, concomitant use should generally be avoided.', 'Metabolism', 'Given the potential for diminished pharmacologic effects of trabectedin in the presence of potent CYP450 3A4 inducers, concomitant use should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477534, 'Tranylcypromine', 'Phenytoin', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317477, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477535, 'Treprostinil', 'Phenytoin', 'Moderate', 'Coadministration with inducers of CYP450 2C8 may decrease the plasma concentrations of treprostinil, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Pharmacologic response to treprostinil should be monitored more closely whenever a CYP450 2C8 inducer is added to or withdrawn from therapy, and the treprostinil dosage adjusted as necessary. Patients should be advised to notify their physician if they experience worsening of their condition.', 'Metabolism', 'Pharmacologic response to treprostinil should be monitored more closely whenever a CYP450 2C8 inducer is added to or withdrawn from therapy, and the treprostinil dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477536, 'Triazolam', 'Phenytoin', 'Moderate', 'Coadministration with some benzodiazepines may alter the serum concentrations of phenytoin. Phenytoin toxicity has been reported in patients treated with various benzodiazepines, including clobazam, chlordiazepoxide, clonazepam, and diazepam. Conversely, phenytoin may reduce the plasma concentrations of some benzodiazepines by inducing their metabolism via hepatic microsomal enzymes.', 'DDInter', 1767317477, 'Pharmacologic response and serum hydantoin levels should be monitored more closely whenever a benzodiazepine is added to or withdrawn from therapy, and the hydantoin dosage adjusted as necessary. Patients should be advised to contact their physician if they experience symptoms of hydantoin toxicity such as nausea, vomiting, tremors, ataxia, lethargy, slurred speech, visual disturbances, or changes in mental status. The potential for diminished or inadequate benzodiazepine effects should be considered during concomitant use with phenytoin.', 'Metabolism', 'Pharmacologic response and serum hydantoin levels should be monitored more closely whenever a benzodiazepine is added to or withdrawn from therapy, and the hydantoin dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477537, 'Trifluoperazine', 'Phenytoin', 'Moderate', 'Concurrent use of phenothiazines and phenytoin may increase the effect of phenytoin and decrease the effect of the phenothiazine.', 'DDInter', 1767317477, 'Close monitoring for clinical and laboratory evidence of phenytoin toxicity is indicated. Patients should be advised to notify their physician if they experience symptoms of possible hydantoin toxicity, including drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia.', 'Others', 'Close monitoring for clinical and laboratory evidence of phenytoin toxicity is indicated.', NULL, NULL, 'Moderate', 'pharmacodynamic');
