INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1477542, 'Trimipramine', 'Phenytoin', 'Moderate', 'Tricyclic antidepressants may increase serum phenytoin levels. In vitro studies suggest the mechanism may involve inhibition of CYP450 2C19-catalyzed phenytoin p-hydroxylation by tricyclic antidepressants, especially imipramine and amitriptyline. Phenytoin may induce the hepatic metabolism of desipramine resulting in decreased plasma concentrations.', 'DDInter', 1767317477, 'Clinical monitoring of patient response, tolerance, and serum phenytoin concentrations is recommended. Patients should be advised to notify their doctor if they experience loss of seizure control or symptoms of phenytoin toxicity (drowsiness, visual disturbances, change in mental status, nausea, or ataxia). Phenytoin dosage may need to be adjusted.', 'Metabolism', 'Clinical monitoring of patient response, tolerance, and serum phenytoin concentrations is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
