        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1478065, 'Fenoldopam', 'Pindolol', 'Moderate', 'The use of fenoldopam in combination with beta-blockers has not been studied. Theoretically, beta-blockers may potentiate the risk of severe, symptomatic hypotension by inhibiting the sympathetic reflex response to fenoldopam-induced blood pressure decreases.', NULL, 'Concomitant use of fenoldopam with beta-blockers should be avoided if possible.', NULL, 'Concomitant use of fenoldopam with beta-blockers should be avoided if possible.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
