INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1478088, 'Glycerol phenylbutyrate', 'Pindolol', 'Moderate', 'Coadministration of glycerol phenylbutyrate may lead to increased concentrations of CYP450 2D6 substrates. The proposed mechanism is inhibition of CYP450 2D6 by phenylbutyrate, the active moiety of glycerol phenylbutyrate, which has been shown to be an in vitro inhibitor of this isoenzyme.', NULL, 'Caution is recommended if glycerol phenylbutyrate is administered with medicines that are substrates of CYP450 2D6, such as codeine, dextromethorphan, and some beta blockers (including metoprolol, carvedilol, labetalol, propranolol, and nebivolol).', NULL, 'Caution is recommended if glycerol phenylbutyrate is administered with medicines that are substrates of CYP450 2D6, such as codeine, dextromethorphan, and some beta blockers (including metoprolol, carvedilol, labetalol, propranolol, and nebivolol). Dosage adjustments as well as clinical and laboratory monitoring may be appropriate.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
