        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1478296, 'Voriconazole', 'Pirfenidone', 'Moderate', 'Coadministration with moderate or potent inhibitors of CYP450 2C9, 2C19, 2D6, and/or 2E1 may increase the plasma concentrations of pirfenidone, especially when used in addition to a moderate or potent CYP450 1A2 inhibitor. Pirfenidone is primarily (70% to 80%) metabolized by CYP450 1A2, with minor contribution from CYP450 2C9, 2C19, 2D6, and 2E1.', NULL, 'Concomitant use of pirfenidone with moderate or potent inhibitors of CYP450 2C9, 2C19, 2D6, and/or 2E1 in addition to a moderate or potent inhibitor of CYP450 1A2 (e.', NULL, 'Concomitant use of pirfenidone with moderate or potent inhibitors of CYP450 2C9, 2C19, 2D6, and/or 2E1 in addition to a moderate or potent inhibitor of CYP450 1A2 (e.g., certain fluoroquinolones, oral contraceptives, deferasirox, methoxsalen, mexiletine, thiabendazole, ticlopidine, zileuton) should be avoided. No particular precaution is necessary if pirfenidone and CYP450 2C9, 2C19, 2D6, and/or 2E1 inhibitors are coadministered without a moderate or potent CYP450 1A2 inhibitor.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1478297, 'Zileuton', 'Pirfenidone', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 may increase the plasma concentrations of pirfenidone, which is primarily (70% to 80%) metabolized by the isoenzyme. Other CYP450 isoenzymes also contribute to a minor extent, including CYP450 2C9, 2C19, 2D6, and 2E1. Concomitant inhibition of one or more of these isoenzymes in addition to CYP450 1A2 is expected to further increase the magnitude of interaction.', NULL, 'Caution is advised when pirfenidone is used concomitantly with CYP450 1A2 inhibitors.', NULL, 'Caution is advised when pirfenidone is used concomitantly with CYP450 1A2 inhibitors. Patients should be closely monitored for adverse reactions such as hepatotoxicity, photosensitivity, rash, nausea, diarrhea, vomiting and dyspepsia, and consideration be given to dosage reduction, brief interruption, or permanent discontinuation of pirfenidone if clinically necessary in accordance with the product labeling. Coadministration of pirfenidone with CYP450 1A2 inhibitors in addition to inhibitors of other CYP450 isoenzymes involved in the metabolism of pirfenidone should be avoided, including significant inhibitors of CYP450 2C9 (e.g., amiodarone, fluconazole, imatinib, gemfibrozil, miconazole), 2C19 (e.g., chloramphenicol, cimetidine, esomeprazole, fluconazole, lansoprazole, omeprazole), and/or 2D6 (e.g., bupropion, cinacalcet, fluoxetine, paroxetine, quinidine, terbinafine).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
