        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1478515, 'Amoxapine', 'Pitolisant', 'Moderate', 'Because pitolisant enhances the activity of histaminergic neurons in the brain, centrally acting histamine H1-receptor antagonists or medicines with H1-receptor antagonist properties (e.g., tri- and tetracyclic antidepressants) may impair the efficacy of pitolisant.', NULL, 'According to the manufacturer, coadministration of pitolisant with centrally acting H1-receptor antagonists or tri- and tetracyclic antidepressants should be avoided.', NULL, 'According to the manufacturer, coadministration of pitolisant with centrally acting H1-receptor antagonists or tri- and tetracyclic antidepressants should be avoided.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
