INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1479190, 'Sodium bicarbonate', 'Ponatinib', 'Minor', 'Coadministration with drugs that elevate the gastric pH such as proton pump inhibitors, H2-receptor antagonists, and antacids may decrease the oral bioavailability of ponatinib and reduce its concentrations in plasma. The aqueous solubility of ponatinib has been shown to be pH-dependent, with higher pH resulting in decreased solubility.', 'DDInter', 1767317477, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
