INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1479238, 'Ustekinumab', 'Ponatinib', 'Moderate', 'The use of ustekinumab with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', 'DDInter', 1767317477, 'Caution is advised if ustekinumab must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa. Close clinical and laboratory monitoring for the development of severe hematologic adverse effects is recommended both during and after discontinuation of therapy.', 'Others', 'Caution is advised if ustekinumab must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1479239, 'Valganciclovir', 'Ponatinib', 'Moderate', 'The use of ganciclovir with other potentially myelotoxic and/or nephrotoxic agents may increase the risk and severity of hematologic toxicity and/or nephrotoxicity due to both additive pharmacodynamic effects and additive effects on the kidney. Ganciclovir alone may cause severe leucopenia, neutropenia, anemia, thrombocytopenia, pancytopenia, bone marrow depression, aplastic anemia, increased serum creatinine levels, and acute renal failure, and the risk may theoretically increase when coadministered with other myelotoxic or nephrotoxic therapy.', 'DDInter', 1767317477, 'Ganciclovir or its prodrug, valganciclovir, should generally not be combined with other drugs associated with myelosuppression and/or nephrotoxicity unless the benefits are anticipated to outweigh the potential risks. Extreme caution is advised if they are used in patients who have recently received or are receiving treatment with myelotoxic and/or nephrotoxic drugs, and vice versa. If coadministration is required, reduced dosages of one or more of the drugs may be required, and the patient should be monitored for the development of hematologic and/or renal adverse effects both during and after discontinuation of therapy.', 'Synergism', 'Ganciclovir or its prodrug, valganciclovir, should generally not be combined with other drugs associated with myelosuppression and/or nephrotoxicity unless the benefits are anticipated to outweigh the potential risks.', NULL, NULL, 'Moderate', 'pharmacodynamic');
