INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1479253, 'Zafirlukast', 'Ponatinib', 'Moderate', 'The risk of peripheral neuropathy may be increased during concurrent use of two or more agents that are associated with this adverse effect. Patient risk factors include diabetes and age older than 60 years.', 'DDInter', 1767317477, 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet. Since the development of peripheral neuropathy may be dose-related for many drugs, the recommended dosages should generally not be exceeded. Consideration should be given to dosage reduction or immediate discontinuation of these medications in patients who develop peripheral neuropathy to limit further damage.', 'Synergism', 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1479254, 'Zalcitabine', 'Ponatinib', 'Moderate', 'Zalcitabine can cause peripheral neuropathy in up to one-third of patients with advanced HIV disease, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk and/or severity of nerve damage. Zalcitabine-related peripheral neuropathy is a sensorimotor neuropathy characterized initially by numbness and burning dysesthesia involving the distal extremities. These symptoms may be followed by sharp shooting pains or severe continuous burning pain if the drug is not withdrawn, and progress to severe pain requiring narcotic analgesics. The neuropathy is potentially irreversible. However, with prompt discontinuation of zalcitabine, it is usually slowly reversible, although symptoms may initially progress following discontinuation.', 'DDInter', 1767317477, 'Use of zalcitabine with other drugs that have the potential to cause peripheral neuropathy should be avoided whenever possible. Otherwise, careful monitoring is recommended for symptoms of neuropathy such as burning, tingling, pain, numbness, or weakness in the extremities, particularly in patients with a low CD4 cell count or diabetes. Since the development of peripheral neuropathy appears to be dose-related, the recommended dosage of zalcitabine should not be exceeded. Patients should be advised to promptly discontinue zalcitabine therapy and contact their physician if neuropathy develops. Therapy may be reinstituted following resolution of neuropathy symptoms, but dosage should be reduced to one-half the initially recommended dosage. Zalcitabine should be permanently discontinued in patients who develop severe peripheral neuropathy during treatment.', 'Synergism', 'Use of zalcitabine with other drugs that have the potential to cause peripheral neuropathy should be avoided whenever possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
