INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1479374, 'Eravacycline', 'Posaconazole', 'Minor', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of eravacycline, which undergoes oxidation by CYP450 3A4 and flavin monoxygenase (FMO).', 'DDInter', 1767317477, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1479375, 'Erdafitinib', 'Posaconazole', 'Major', 'Coadministration of erdafitinib with potent inhibitors of CYP450 2C9 or 3A4 may increase the plasma concentrations and risk of toxicity of erdafitinib, which is a substrate of these isoenzymes.', 'DDInter', 1767317477, 'Coadministration of erdafitinib with potent inhibitors of CYP450 2C9 or 3A4 should generally be avoided. If concomitant use is unavoidable, patients should be closely monitored for the development of adverse effects. Dosage adjustments as well as clinical and laboratory monitoring of erdafitinib should be considered whenever a CYP450 2C9 or 3A4 inhibitor is added to or withdrawn from therapy.', 'Metabolism', 'Coadministration of erdafitinib with potent inhibitors of CYP450 2C9 or 3A4 should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1479376, 'Ergometrine', 'Posaconazole', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 including azole antifungal agents may significantly increase the plasma concentrations of ergot derivatives, which are primarily metabolized by the isoenzyme. Clinical ergotism has been reported, which can lead to gangrene and myocardial infarction in severe cases.', 'DDInter', 1767317477, 'Given the potential for ergot toxicity characterized by peripheral vasospasm, ischemia, thrombosis, tachycardia and hypertension, concomitant use of ergot derivatives with itraconazole, ketoconazole, posaconazole, or voriconazole is considered contraindicated. Some authorities consider concomitant administration of ergot derivatives and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole. Use with other azole antifungal agents, including fluconazole at high multiple doses, should probably be avoided if possible.', 'Metabolism', 'Given the potential for ergot toxicity characterized by peripheral vasospasm, ischemia, thrombosis, tachycardia and hypertension, concomitant use of ergot derivatives with itraconazole, ketoconazole, posaconazole, or voriconazole is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
