INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1479449, 'Posaconazole', 'Methysergide', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 including azole antifungal agents may significantly increase the plasma concentrations of ergot derivatives, which are primarily metabolized by the isoenzyme. Clinical ergotism has been reported, which can lead to gangrene and myocardial infarction in severe cases.', 'DDInter', 1767317477, 'Given the potential for ergot toxicity characterized by peripheral vasospasm, ischemia, thrombosis, tachycardia and hypertension, concomitant use of ergot derivatives with itraconazole, ketoconazole, posaconazole, or voriconazole is considered contraindicated. Some authorities consider concomitant administration of ergot derivatives and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole. Use with other azole antifungal agents, including fluconazole at high multiple doses, should probably be avoided if possible.', 'Metabolism', 'Given the potential for ergot toxicity characterized by peripheral vasospasm, ischemia, thrombosis, tachycardia and hypertension, concomitant use of ergot derivatives with itraconazole, ketoconazole, posaconazole, or voriconazole is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
