INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1479454, 'Posaconazole', 'Phenobarbital', 'Moderate', 'Phenytoin and other enzyme-inducing anticonvulsants may decrease the plasma concentration of posaconazole. The mechanism is induction of posaconazole clearance via the UDP-glucuronosyltransferase glucuronidation and P-glycoprotein efflux pathways.', 'DDInter', 1767317477, 'Use of posaconazole in combination with enzyme-inducing anticonvulsants should generally be avoided unless the potential benefit outweighs the risk. If coadministration is necessary, patient response to the antifungal agent must be closely monitored. If phenytoin is used, pharmacologic response and serum phenytoin levels should also be monitored closely following initiation or discontinuation of posaconazole, and the phenytoin dosage adjusted as necessary.', 'Metabolism', 'Use of posaconazole in combination with enzyme-inducing anticonvulsants should generally be avoided unless the potential benefit outweighs the risk.', NULL, NULL, 'Moderate', 'pharmacodynamic');
