INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1479547, 'Tucatinib', 'Posaconazole', 'Moderate', 'Coadministration with tucatinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The mechanism likely involves tucatinib-mediated inhibition of P-gp. Increased exposure to the P-gp substrate may increase the risk of toxicity.', 'DDInter', 1767317477, 'Caution is advised if tucatinib is used concomitantly with P-gp substrates, particularly those with a narrow therapeutic index. A dose reduction of the P-gp substrates as well as clinical and laboratory monitoring may be appropriate.', 'Absorption', 'Caution is advised if tucatinib is used concomitantly with P-gp substrates, particularly those with a narrow therapeutic index.', NULL, NULL, 'Moderate', 'pharmacodynamic');
