INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1479555, 'Venetoclax', 'Posaconazole', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of venetoclax, which is primarily metabolized by the isoenzyme. Additionally, inhibition of P-glycoprotein (P-gp) and breast cancer resistance protein (BCRP) efflux transporters by ketoconazole may contribute. Increased venetoclax exposure may potentiate the risk of tumor lysis syndrome, particularly at initiation of therapy and during the dosage ramp-up phase, as well as other adverse effects such as diarrhea, nausea, vomiting, neutropenia, anemia, and thrombocytopenia.', 'DDInter', 1767317477, 'Concomitant use of potent CYP450 3A4 inhibitors at initiation and during the dosage ramp-up phase of venetoclax is considered contraindicated. If a potent CYP450 3A4 inhibitor is required after the ramp-up phase, the manufacturer recommends that venetoclax dosage be reduced by at least 75%. The dosage used prior to initiating the CYP450 3A4 inhibitor may be resumed 2 to 3 days after discontinuation of the inhibitor.', 'Metabolism', 'Concomitant use of potent CYP450 3A4 inhibitors at initiation and during the dosage ramp-up phase of venetoclax is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1479556, 'Venlafaxine', 'Posaconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 such as azole antifungal agents and ketolide/macrolide antibiotics may increase the plasma concentrations of venlafaxine and its metabolite, O-desmethylvenlafaxine (ODV). The possibility of prolonged and/or increased pharmacologic effects of venlafaxine should be considered. Venlafaxine may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval such as azole antifungal agents and ketolide/macrolide antibiotics may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Caution is recommended when venlafaxine is used with CYP450 3A4 inhibitors that can prolong the QT interval such as azole antifungal agents and ketolide/macrolide antibiotics. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', 'Caution is recommended when venlafaxine is used with CYP450 3A4 inhibitors that can prolong the QT interval such as azole antifungal agents and ketolide/macrolide antibiotics.', NULL, NULL, 'Moderate', 'pharmacodynamic');
