INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1479563, 'Voxelotor', 'Posaconazole', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the systemic exposure (AUC) to voxelotor, which has been shown to be a substrate of this isoenzyme.', 'DDInter', 1767317477, 'The use of voxelotor with potent CYP450 3A4 inhibitors should generally be avoided. Alternative therapeutic agents with less inhibitory potential should be considered whenever possible. If coadministration is required, the manufacturer recommends reducing the voxelotor dose to 1000 mg once daily. Patients should be closely monitored for signs of voxelotor-related adverse effects, including headache, diarrhea, abdominal pain, nausea, and vomiting.', 'Metabolism', 'The use of voxelotor with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1479564, 'Zaleplon', 'Posaconazole', 'Moderate', 'Coadministration with posaconazole may significantly increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by posaconazole.', 'DDInter', 1767317477, 'Caution is advised if posaconazole must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever posaconazole is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if posaconazole must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1479565, 'Zanubrutinib', 'Posaconazole', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of zanubrutinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'The manufacturer recommends reducing the dosage of zanubrutinib to 80 mg once daily when coadministered with potent CYP450 3A4 inhibitors. Patients should be closely monitored for development of zanubrutinib-related toxicities, and further dosage adjustments made or treatment withheld as needed in accordance with the product labeling. Following discontinuation of the CYP450 3A4 inhibitor, the previous dosage of zanubrutinib should be resumed.', 'Metabolism', 'The manufacturer recommends reducing the dosage of zanubrutinib to 80 mg once daily when coadministered with potent CYP450 3A4 inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1479566, 'Ziprasidone', 'Posaconazole', 'Major', 'Ziprasidone can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Excessive parasympatholytic effects may include paralytic ileus, hyperthermia, mydriasis, blurred vision, tachycardia, urinary retention, psychosis, and seizures.', 'DDInter', 1767317477, 'Coadministration of ziprasidone with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', 'Coadministration of ziprasidone with other drugs that can prolong the QT interval is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1479567, 'Zonisamide', 'Posaconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of zonisamide, which is metabolized by the isoenzyme. While clinical data are lacking, the possibility of prolonged and/or increased pharmacologic effects of zonisamide should be considered.', 'DDInter', 1767317477, 'Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. Patients should be advised to notify their physician if they experience symptoms such as severe drowsiness, confusion, loss of coordination, depression, aphasia, decreased sweating, fever, loss of seizure control, or slow pulse.', 'Metabolism', 'Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1479568, 'Cinoxacin', 'Potassium citrate', 'Moderate', 'Urinary alkalinizers such as citrates may decrease the solubility of fluoroquinolones in the urine and increase the risk of crystalluria.', 'DDInter', 1767317477, 'Concomitant use should generally be avoided. Patients receiving this combination should be monitored for signs of renal toxicity and crystalluria.', 'Excretion', 'Concomitant use should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1479569, 'Bromfenac', 'Potassium Iodide', 'Moderate', 'Concomitant use of nonsteroidal anti-inflammatory drugs (NSAIDs) with potassium salts may increase the risk of hyperkalemia. NSAIDs may produce potassium retention by reducing renal synthesis of prostaglandin E and impairing the renin-angiotensin system.', 'DDInter', 1767317477, 'Closely monitor potassium levels in patients receiving both potassium salts and NSAID therapy, especially those with renal impairment, diabetes, older age, severe or worsening heart failure, dehydration, or concomitant therapy with other agents that increase serum potassium (e.g., beta-blockers, cyclosporine, heparin, tacrolimus, and trimethoprim). Patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as nausea, vomiting, weakness, listlessness, tingling of the extremities, paralysis, confusion, weak pulse, and a slow or irregular heartbeat.', 'Synergism', 'Closely monitor potassium levels in patients receiving both potassium salts and NSAID therapy, especially those with renal impairment, diabetes, older age, severe or worsening heart failure, dehydration, or concomitant therapy with other agents that increase serum potassium (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
