INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1479657, 'Enzalutamide', 'Pralsetinib', 'Major', 'Coadministration with potent inducers of CYP450 3A may significantly decrease the plasma concentrations of pralsetinib which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of pralsetinib with potent CYP450 3A inducers should generally be avoided. If coadministration with potent CYP450 3A inducers is necessary, the initial dose of pralsetinib should be doubled starting on Day 7 of coadministration with the strong CYP450 3A4 inducer. Once the potent CYP450 3A inducer has been discontinued for at least 14 days, the pralsetinib dose taken prior to initiating the potent CYP450 3A4 inducer may be resumed.', 'Metabolism', 'Concomitant use of pralsetinib with potent CYP450 3A inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
