INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1479660, 'Etonogestrel', 'Pralsetinib', 'Moderate', 'RECOMMENDED: Coadministration with pralsetinib may render hormonal contraceptives ineffective. The mechanism for the potential interaction has not been established and there are no clinical data regarding the use of pralsetinib with hormonal contraceptives. Pralsetinib can cause fetal harm when administered to a pregnant woman.', 'DDInter', 1767317477, 'Until further data are available, females of reproductive potential should be advised to use effective non-hormonal contraception during treatment and for 2 weeks after the final dose. In addition, men receiving pralsetinib should use effective contraception during treatment and for 1 week after the final dose.', 'Others', 'Until further data are available, females of reproductive potential should be advised to use effective non-hormonal contraception during treatment and for 2 weeks after the final dose.', NULL, NULL, 'Moderate', 'pharmacodynamic');
