    ) VALUES (1479695, 'Telotristat ethyl', 'Pralsetinib', 'Moderate', 'Coadministration with moderate inducers of CYP450 3A may decrease the plasma concentrations of pralsetinib which is primarily metabolized by the isoenzyme.', NULL, 'The potential for diminished pharmacologic effects of pralsetinib should be considered during coadministration with moderate CYP450 3A4 inducers.', NULL, 'The potential for diminished pharmacologic effects of pralsetinib should be considered during coadministration with moderate CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
