        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1479996, 'Tirofiban', 'Prasugrel', 'Major', 'The combination of glycoprotein IIb/IIIa (GP IIb/IIIa) inhibitors and other drugs that inhibit platelet aggregation may significantly increase the risk of bleeding, especially from arterial puncture sites. The mechanism is synergistic pharmacologic effects.', NULL, 'Extraordinary precautions are recommended if these drugs must be used together, including monitoring platelet counts and hemoglobin concentrations.', NULL, 'Extraordinary precautions are recommended if these drugs must be used together, including monitoring platelet counts and hemoglobin concentrations. In addition, invasive procedures, such as urinary catheterization, nasogastric or nasotracheal intubation, and even venous punctures should be avoided or minimized. Non-compressible venous access sites, such as jugular or subclavian sites, should be avoided.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
