        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1480366, 'Telaprevir', 'Prednisone', 'Moderate', 'Coadministration with telaprevir may increase the plasma concentrations of systemic corticosteroids. The mechanism involves telaprevir inhibition of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of most steroids. No pharmacokinetic data are currently available, although telaprevir is a potent CYP450 3A4 inhibitor and may interact significantly.', NULL, 'The use of systemic corticosteroids in combination with telaprevir is not recommended.', NULL, 'The use of systemic corticosteroids in combination with telaprevir is not recommended. Systemic corticosteroids should not be used to treat telaprevir-induced rash.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
